import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../utils/auth';

export default function Register() {
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'Lecturer'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const navigate = useNavigate();
  const auth = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));

    // Clear error when user starts typing
    if (error) setError('');
  };

  async function submit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Validation
    if (form.password !== form.confirmPassword) {
      setError('Passwords do not match');
      setLoading(false);
      return;
    }

    if (form.password.length < 6) {
      setError('Password must be at least 6 characters long');
      setLoading(false);
      return;
    }

    try {
      const { confirmPassword, ...submitData } = form;
      const res = await axios.post('http://localhost:5000/api/auth/register', submitData);

      if (res.data.success) {
        // Auto-login after successful registration
        auth.saveLogin(res.data.data.token, res.data.data.user);
        navigate('/');
      }
    } catch (err) {
      setError(err.response?.data?.error || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className="container-fluid py-5">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6">
          <div className="card shadow border-0">
            <div className="card-header bg-info text-white text-center py-4">
              <h4 className="mb-0">ðŸ‘¤ Register for LUCT</h4>
              <small>Create your account to start reporting</small>
            </div>

            <div className="card-body p-4">
              {error && (
                <div className="alert alert-danger" role="alert">
                  {error}
                </div>
              )}

              <form onSubmit={submit}>
                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label className="form-label fw-semibold">First Name</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      name="first_name"
                      value={form.first_name}
                      onChange={handleChange}
                      placeholder="Enter your first name"
                    />
                  </div>

                  <div className="col-md-6 mb-3">
                    <label className="form-label fw-semibold">Last Name</label>
                    <input 
                      type="text" 
                      className="form-control" 
                      name="last_name"
                      value={form.last_name}
                      onChange={handleChange}
                      placeholder="Enter your last name"
                    />
                  </div>
                </div>

                <div className="mb-3">
                  <label className="form-label fw-semibold">Email Address</label>
                  <input 
                    type="email" 
                    className="form-control" 
                    name="email"
                    value={form.email}
                    onChange={handleChange}
                    required 
                    placeholder="Enter your email address"
                  />
                </div>

                <div className="row">
                  <div className="col-md-6 mb-3">
                    <label className="form-label fw-semibold">Password</label>
                    <input 
                      type="password" 
                      className="form-control" 
                      name="password"
                      value={form.password}
                      onChange={handleChange}
                      required 
                      placeholder="Create a password"
                      minLength="6"
                    />
                    <div className="form-text">Minimum 6 characters</div>
                  </div>

                  <div className="col-md-6 mb-3">
                    <label className="form-label fw-semibold">Confirm Password</label>
                    <input 
                      type="password" 
                      className="form-control" 
                      name="confirmPassword"
                      value={form.confirmPassword}
                      onChange={handleChange}
                      required 
                      placeholder="Confirm your password"
                    />
                  </div>
                </div>

                <div className="mb-4">
                  <label className="form-label fw-semibold">Role</label>
                  <select 
                    className="form-select" 
                    name="role"
                    value={form.role} 
                    onChange={handleChange}
                  >
                    <option value="Lecturer">Lecturer</option>
                    <option value="Student">Student</option>
                    <option value="PRL">PRL</option>
                    <option value="PL">PL</option>
                  </select>
                  <div className="form-text">
                    Select your role in the institution
                  </div>
                </div>

                <button 
                  className="btn btn-info text-white btn-lg w-100 py-2" 
                  type="submit"
                  disabled={loading}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      Creating Account...
                    </>
                  ) : (
                    'Create Account'
                  )}
                </button>
              </form>

              <div className="text-center mt-4">
                <p className="text-muted">
                  Already have an account?{' '}
                  <Link to="/login" className="text-info text-decoration-none">
                    Login here
                  </Link>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
