import React, { useEffect, useState } from "react";
import { API_ENDPOINTS, apiCall, createAuthHeaders } from "../utils/api";

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchReports = async () => {
      try {
        const data = await apiCall(API_ENDPOINTS.REPORTS, {
          ...createAuthHeaders(token),
        });
        setReports(data.data || []);
      } catch (err) {
        setError("Failed to fetch reports");
      }
    };
    if (token) fetchReports();
  }, [token]);

  return (
    <div className="page-container">
      <h2>Reports</h2>
      {error && <p className="error">{error}</p>}
      {reports.length === 0 ? (
        <p>No reports available</p>
      ) : (
        <table className="reports-table">
          <thead>
            <tr>
              <th>Course</th>
              <th>Class</th>
              <th>Faculty</th>
              <th>Week</th>
              <th>Lecture Date</th>
              <th>Actual Present</th>
              <th>Lecturer</th>
            </tr>
          </thead>
          <tbody>
            {reports.map((r) => (
              <tr key={r.id}>
                <td>{r.course_name}</td>
                <td>{r.class_name}</td>
                <td>{r.faculty_name}</td>
                <td>{r.week_of_reporting}</td>
                <td>{new Date(r.lecture_date).toLocaleDateString()}</td>
                <td>{r.actual_present}</td>
                <td>{r.lecturer_name}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
};

export default Reports;
