import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { API_ENDPOINTS, apiCall, createAuthHeaders } from "../utils/api";

const ReportForm = () => {
  const [form, setForm] = useState({
    faculty_id: "",
    class_id: "",
    week_of_reporting: "",
    lecture_date: "",
    course_id: "",
    actual_present: "",
    total_registered: "",
    venue: "",
    scheduled_time: "",
    topic: "",
    learning_outcomes: "",
    recommendations: "",
  });

  const [faculties, setFaculties] = useState([]);
  const [classes, setClasses] = useState([]);
  const [courses, setCourses] = useState([]);
  const [error, setError] = useState("");
  const navigate = useNavigate();

  const token = localStorage.getItem("token");

  // Fetch faculties, classes, courses
  useEffect(() => {
    const fetchData = async () => {
      try {
        const facultiesRes = await apiCall(API_ENDPOINTS.FACULTIES, {
          ...createAuthHeaders(token),
        });
        setFaculties(facultiesRes.data);

        const classesRes = await apiCall(API_ENDPOINTS.CLASSES, {
          ...createAuthHeaders(token),
        });
        setClasses(classesRes.data);

        const coursesRes = await apiCall(API_ENDPOINTS.COURSES, {
          ...createAuthHeaders(token),
        });
        setCourses(coursesRes.data);
      } catch (err) {
        setError("Failed to load dropdown data");
      }
    };
    if (token) fetchData();
  }, [token]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError("");

    try {
      const data = await apiCall(API_ENDPOINTS.REPORTS, {
        method: "POST",
        data: form,
        ...createAuthHeaders(token),
      });

      if (data?.success) {
        navigate("/reports");
      } else {
        setError(data.message || "Failed to submit report");
      }
    } catch (err) {
      setError(err.message || "Failed to submit report");
    }
  };

  return (
    <div className="form-container">
      <h2>Submit Report</h2>
      {error && <p className="error">{error}</p>}

      <form onSubmit={handleSubmit}>
        <select
          name="faculty_id"
          value={form.faculty_id}
          onChange={handleChange}
          required
        >
          <option value="">Select Faculty</option>
          {faculties.map((f) => (
            <option key={f.id} value={f.id}>
              {f.name}
            </option>
          ))}
        </select>

        <select
          name="class_id"
          value={form.class_id}
          onChange={handleChange}
          required
        >
          <option value="">Select Class</option>
          {classes.map((c) => (
            <option key={c.id} value={c.id}>
              {c.class_name}
            </option>
          ))}
        </select>

        <select
          name="course_id"
          value={form.course_id}
          onChange={handleChange}
          required
        >
          <option value="">Select Course</option>
          {courses.map((c) => (
            <option key={c.id} value={c.id}>
              {c.course_name}
            </option>
          ))}
        </select>

        <input
          type="number"
          name="week_of_reporting"
          placeholder="Week of Reporting (1-52)"
          value={form.week_of_reporting}
          onChange={handleChange}
          required
        />

        <input
          type="date"
          name="lecture_date"
          value={form.lecture_date}
          onChange={handleChange}
          required
        />

        <input
          type="number"
          name="actual_present"
          placeholder="Students Present"
          value={form.actual_present}
          onChange={handleChange}
          required
        />

        <input
          type="number"
          name="total_registered"
          placeholder="Total Registered"
          value={form.total_registered}
          onChange={handleChange}
        />

        <input
          type="text"
          name="venue"
          placeholder="Venue"
          value={form.venue}
          onChange={handleChange}
        />

        <input
          type="text"
          name="scheduled_time"
          placeholder="Scheduled Time"
          value={form.scheduled_time}
          onChange={handleChange}
        />

        <textarea
          name="topic"
          placeholder="Topic"
          value={form.topic}
          onChange={handleChange}
        />

        <textarea
          name="learning_outcomes"
          placeholder="Learning Outcomes"
          value={form.learning_outcomes}
          onChange={handleChange}
        />

        <textarea
          name="recommendations"
          placeholder="Recommendations"
          value={form.recommendations}
          onChange={handleChange}
        />

        <button type="submit">Submit Report</button>
      </form>
    </div>
  );
};

export default ReportForm;
