import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useAuth } from '../utils/auth';

export default function Reports() {
  const { token, user } = useAuth();
  const [reports, setReports] = useState([]);
  const [stats, setStats] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) return;
    fetchReports();
    fetchStats();
  }, [token]);

  const fetchReports = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/data/reports', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        setReports(res.data.data || []);
      }
    } catch (err) {
      setError('Failed to load reports');
      console.error('Fetch reports error:', err);
    } finally {
      setLoading(false);
    }
  };

  const fetchStats = async () => {
    try {
      const res = await axios.get('http://localhost:5000/api/data/reports/stats', {
        headers: { Authorization: `Bearer ${token}` }
      });

      if (res.data.success) {
        setStats(res.data.data);
      }
    } catch (err) {
      console.error('Fetch stats error:', err);
    }
  };

  const getAttendancePercentage = (actual, total) => {
    if (!actual || !total) return 0;
    return Math.round((actual / total) * 100);
  };

  const getAttendanceColor = (percentage) => {
    if (percentage >= 80) return 'success';
    if (percentage >= 60) return 'warning';
    return 'danger';
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center py-5">
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading reports...</span>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      {/* Header */}
      <div className="row mb-4">
        <div className="col-12">
          <div className="d-flex justify-content-between align-items-center">
            <div>
              <h4 className="text-primary mb-1">📊 Lecture Reports</h4>
              <p className="text-muted mb-0">
                {user?.role === 'Lecturer' ? 'Your submitted reports' : 'All system reports'}
              </p>
            </div>
            {stats && (
              <div className="text-end">
                <h5 className="mb-1">{stats.total_reports} Reports</h5>
                <small className="text-muted">Total Submitted</small>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Stats Overview */}
      {stats && (
        <div className="row mb-4">
          <div className="col-xl-3 col-md-6 mb-4">
            <div className="card border-left-primary shadow h-100 py-2">
              <div className="card-body">
                <div className="row no-gutters align-items-center">
                  <div className="col mr-2">
                    <div className="text-xs font-weight-bold text-primary text-uppercase mb-1">
                      Total Reports
                    </div>
                    <div className="h5 mb-0 font-weight-bold text-gray-800">
                      {stats.total_reports}
                    </div>
                  </div>
                  <div className="col-auto">
                    <i className="fas fa-clipboard-list fa-2x text-gray-300"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="col-xl-3 col-md-6 mb-4">
            <div className="card border-left-success shadow h-100 py-2">
              <div className="card-body">
                <div className="row no-gutters align-items-center">
                  <div className="col mr-2">
                    <div className="text-xs font-weight-bold text-success text-uppercase mb-1">
                      Avg Attendance
                    </div>
                    <div className="h5 mb-0 font-weight-bold text-gray-800">
                      {Math.round(stats.avg_attendance)}%
                    </div>
                  </div>
                  <div className="col-auto">
                    <i className="fas fa-users fa-2x text-gray-300"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="col-xl-3 col-md-6 mb-4">
            <div className="card border-left-info shadow h-100 py-2">
              <div className="card-body">
                <div className="row no-gutters align-items-center">
                  <div className="col mr-2">
                    <div className="text-xs font-weight-bold text-info text-uppercase mb-1">
                      Active Lecturers
                    </div>
                    <div className="h5 mb-0 font-weight-bold text-gray-800">
                      {stats.active_lecturers}
                    </div>
                  </div>
                  <div className="col-auto">
                    <i className="fas fa-chalkboard-teacher fa-2x text-gray-300"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>

          <div className="col-xl-3 col-md-6 mb-4">
            <div className="card border-left-warning shadow h-100 py-2">
              <div className="card-body">
                <div className="row no-gutters align-items-center">
                  <div className="col mr-2">
                    <div className="text-xs font-weight-bold text-warning text-uppercase mb-1">
                      Courses Covered
                    </div>
                    <div className="h5 mb-0 font-weight-bold text-gray-800">
                      {stats.courses_covered}
                    </div>
                  </div>
                  <div className="col-auto">
                    <i className="fas fa-book fa-2x text-gray-300"></i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Reports Table */}
      <div className="card shadow">
        <div className="card-header bg-white py-3">
          <h6 className="m-0 font-weight-bold text-primary">
            Recent Lecture Reports
          </h6>
        </div>

        <div className="card-body">
          {error && (
            <div className="alert alert-danger" role="alert">
              {error}
            </div>
          )}

          {reports.length === 0 ? (
            <div className="text-center py-5">
              <i className="fas fa-clipboard-list fa-3x text-muted mb-3"></i>
              <h5 className="text-muted">No reports found</h5>
              <p className="text-muted">
                {user?.role === 'Lecturer' 
                  ? 'You haven\'t submitted any reports yet.' 
                  : 'No reports have been submitted yet.'
                }
              </p>
            </div>
          ) : (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead className="table-light">
                  <tr>
                    <th>Report ID</th>
                    <th>Course</th>
                    <th>Class</th>
                    <th>Lecture Date</th>
                    {user?.role !== 'Lecturer' && <th>Lecturer</th>}
                    <th>Attendance</th>
                    <th>Week</th>
                    <th>Status</th>
                  </tr>
                </thead>
                <tbody>
                  {reports.map(report => {
                    const attendancePercent = getAttendancePercentage(
                      report.actual_present, 
                      report.total_registered
                    );
                    const attendanceColor = getAttendanceColor(attendancePercent);

                    return (
                      <tr key={report.id} style={{ cursor: 'pointer' }}>
                        <td>
                          <strong>LR-{report.id.toString().padStart(6, '0')}</strong>
                        </td>
                        <td>
                          <div>
                            <strong>{report.course_name}</strong>
                            <br />
                            <small className="text-muted">{report.course_code}</small>
                          </div>
                        </td>
                        <td>{report.class_name}</td>
                        <td>
                          {new Date(report.lecture_date).toLocaleDateString()}
                        </td>
                        {user?.role !== 'Lecturer' && (
                          <td>{report.lecturer_name}</td>
                        )}
                        <td>
                          <div className="d-flex align-items-center">
                            <div className="progress flex-grow-1 me-2" style={{ height: '6px' }}>
                              <div 
                                className={`progress-bar bg-${attendanceColor}`}
                                style={{ width: `${attendancePercent}%` }}
                              ></div>
                            </div>
                            <small className="text-nowrap">
                              {report.actual_present || 0}/{report.total_registered || 0}
                            </small>
                          </div>
                          <small className={`text-${attendanceColor}`}>
                            {attendancePercent}%
                          </small>
                        </td>
                        <td>
                          <span className="badge bg-secondary">
                            Week {report.week_of_reporting}
                          </span>
                        </td>
                        <td>
                          <span className="badge bg-success">Submitted</span>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
