import React, { useEffect, useState } from 'react';
import { useAuth } from '../utils/auth';
import { api, API_ENDPOINTS } from '../utils/api';

export default function Rating() {
  const { user } = useAuth();
  const [ratings, setRatings] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');

  useEffect(() => {
    fetchRatings();
  }, []);

  const fetchRatings = async () => {
    try {
      setLoading(true);
      const response = await api.get(`${API_ENDPOINTS.RATINGS}?user_id=${user.id}`, true);
      if (response.success) {
        setRatings(response.data);
      }
    } catch (error) {
      console.error('Failed to fetch ratings:', error);
    } finally {
      setLoading(false);
    }
  };

  const submitRating = async () => {
    try {
      const response = await api.post(API_ENDPOINTS.RATINGS, {
        rated_item_id: selectedItem.id,
        rated_item_type: selectedItem.type,
        rating,
        comment,
        rated_by: user.id
      }, true);
      
      if (response.success) {
        setSelectedItem(null);
        setRating(0);
        setComment('');
        fetchRatings();
      }
    } catch (error) {
      console.error('Failed to submit rating:', error);
    }
  };

  const RatingStars = ({ currentRating, onRatingChange }) => {
    return (
      <div className="rating-stars">
        {[1, 2, 3, 4, 5].map(star => (
          <button
            key={star}
            type="button"
            className={`btn btn-star ${star <= currentRating ? 'text-warning' : 'text-muted'}`}
            onClick={() => onRatingChange(star)}
          >
            <i className="fas fa-star fa-2x"></i>
          </button>
        ))}
      </div>
    );
  };

  if (loading) {
    return (
      <div className="container-fluid">
        <div className="d-flex justify-content-center align-items-center py-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading ratings...</span>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      <div className="row mb-4">
        <div className="col-12">
          <h4 className="text-primary mb-1 fw-bold">⭐ Rating System</h4>
          <p className="text-muted">Rate courses, lectures, and reports</p>
        </div>
      </div>

      {/* Rating Modal */}
      {selectedItem && (
        <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Rate {selectedItem.type}</h5>
                <button type="button" className="btn-close" onClick={() => setSelectedItem(null)}></button>
              </div>
              <div className="modal-body text-center">
                <h6>{selectedItem.name}</h6>
                <RatingStars currentRating={rating} onRatingChange={setRating} />
                <div className="mt-3">
                  <label className="form-label">Comments (Optional)</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    placeholder="Add your comments..."
                  ></textarea>
                </div>
                <div className="mt-3 d-flex gap-2 justify-content-center">
                  <button type="button" className="btn btn-secondary" onClick={() => setSelectedItem(null)}>
                    Cancel
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-primary"
                    onClick={submitRating}
                    disabled={rating === 0}
                  >
                    Submit Rating
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <div className="row">
        {/* Rate Courses */}
        <div className="col-md-4 mb-4">
          <div className="card h-100">
            <div className="card-body text-center">
              <i className="fas fa-book fa-3x text-primary mb-3"></i>
              <h5>Rate Courses</h5>
              <p className="text-muted">Provide feedback on courses you've taken</p>
              <button
                className="btn btn-outline-primary"
                onClick={() => setSelectedItem({
                  id: 1,
                  type: 'course',
                  name: 'Web Development'
                })}
              >
                Rate Course
              </button>
            </div>
          </div>
        </div>

        {/* Rate Lectures */}
        <div className="col-md-4 mb-4">
          <div className="card h-100">
            <div className="card-body text-center">
              <i className="fas fa-chalkboard-teacher fa-3x text-success mb-3"></i>
              <h5>Rate Lectures</h5>
              <p className="text-muted">Evaluate lecture quality and content</p>
              <button
                className="btn btn-outline-success"
                onClick={() => setSelectedItem({
                  id: 1,
                  type: 'lecture',
                  name: 'Recent Lecture'
                })}
              >
                Rate Lecture
              </button>
            </div>
          </div>
        </div>

        {/* Rate Reports */}
        <div className="col-md-4 mb-4">
          <div className="card h-100">
            <div className="card-body text-center">
              <i className="fas fa-clipboard-list fa-3x text-info mb-3"></i>
              <h5>Rate Reports</h5>
              <p className="text-muted">Provide feedback on report quality</p>
              <button
                className="btn btn-outline-info"
                onClick={() => setSelectedItem({
                  id: 1,
                  type: 'report',
                  name: 'Lecture Report'
                })}
              >
                Rate Report
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Previous Ratings */}
      <div className="card mt-4">
        <div className="card-header">
          <h5 className="mb-0">Your Previous Ratings</h5>
        </div>
        <div className="card-body">
          {ratings.length > 0 ? (
            <div className="table-responsive">
              <table className="table table-hover">
                <thead>
                  <tr>
                    <th>Item</th>
                    <th>Type</th>
                    <th>Rating</th>
                    <th>Comment</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {ratings.map(rating => (
                    <tr key={rating.id}>
                      <td>{rating.item_name}</td>
                      <td>
                        <span className="badge bg-secondary text-capitalize">
                          {rating.item_type}
                        </span>
                      </td>
                      <td>
                        <div className="text-warning">
                          {'★'.repeat(rating.rating)}{'☆'.repeat(5 - rating.rating)}
                        </div>
                      </td>
                      <td>{rating.comment || 'No comment'}</td>
                      <td>
                        {new Date(rating.created_at).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ) : (
            <div className="text-center py-4">
              <i className="fas fa-star fa-3x text-muted mb-3"></i>
              <p className="text-muted">You haven't submitted any ratings yet.</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
