import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/auth';
import { apiMethods } from '../utils/api';

export default function Rating() {
  const [lecturers, setLecturers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classes, setClasses] = useState([]);
  const [myRatings, setMyRatings] = useState([]);
  const [selectedType, setSelectedType] = useState('lecturer_rating');
  const [selectedLecturerId, setSelectedLecturerId] = useState('');
  const [selectedCourseId, setSelectedCourseId] = useState('');
  const [selectedClassName, setSelectedClassName] = useState('');
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const { user } = useAuth();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [lecturersRes, coursesRes, classesRes] = await Promise.all([
        apiMethods.getLecturers(),
        apiMethods.getCourses(),
        apiMethods.getClasses()
      ]);

      if (lecturersRes.success) setLecturers(lecturersRes.data || []);
      if (coursesRes.success) setCourses(coursesRes.data || []);
      if (classesRes.success) setClasses(classesRes.data || []);

      // Try to get ratings, but don't fail if it errors
      try {
        const ratingsRes = user?.role === 'Lecturer' 
          ? await apiMethods.getLecturerRatings()
          : await apiMethods.getMyRatings();
        
        if (ratingsRes.success) setMyRatings(ratingsRes.data || []);
      } catch (ratingsError) {
        console.warn('Could not load ratings:', ratingsError);
        setMyRatings([]);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load some data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation - both lecturer and course are required
    if (!selectedLecturerId) {
      setError('Please select a lecturer');
      return;
    }
    
    if (!selectedCourseId) {
      setError('Please select a course');
      return;
    }
    
    if (!selectedClassName) {
      setError('Please select a class');
      return;
    }

    if (rating < 1 || rating > 5) {
      setError('Please select a valid rating between 1 and 5');
      return;
    }

    setSubmitting(true);
    setError('');
    setSuccess('');

    try {
      // Create proper rating data structure with BOTH lecturer and course
      const ratingData = {
        rating: Number(rating),
        comment: comment.trim(),
        class_name: selectedClassName,
        rating_type: selectedType, // Use the correct rating type
        lecturer_id: selectedLecturerId,
        course_id: selectedCourseId
      };

      console.log('Submitting rating data:', ratingData);

      const response = await fetch('https://luct-7.onrender.com/api/ratings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(ratingData)
      });

      const responseData = await response.json();
      console.log('Backend response:', responseData);

      if (response.ok) {
        setSuccess('Rating submitted successfully!');
        setRating(5);
        setComment('');
        setSelectedLecturerId('');
        setSelectedCourseId('');
        setSelectedClassName('');
        fetchData(); // Refresh ratings
      } else {
        setError(responseData.message || `Error: ${response.status} ${response.statusText}`);
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      setError(error.message || 'Failed to submit rating. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const hasRated = (lecturerId, courseId, className) => {
    return myRatings.some(rating => 
      rating.lecturer_id === lecturerId && 
      rating.course_id === courseId && 
      rating.class_name === className
    );
  };

  const resetForm = () => {
    setSelectedLecturerId('');
    setSelectedCourseId('');
    setSelectedClassName('');
    setRating(5);
    setComment('');
    setError('');
    setSuccess('');
  };

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <h2 className="text-primary mb-4">
            <i className="fas fa-star me-2"></i>
            Ratings & Reviews
          </h2>

          {/* Success Message */}
          {success && (
            <div className="alert alert-success alert-dismissible fade show" role="alert">
              <i className="fas fa-check-circle me-2"></i>
              {success}
              <button type="button" className="btn-close" onClick={() => setSuccess('')}></button>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="alert alert-danger alert-dismissible fade show" role="alert">
              <i className="fas fa-exclamation-triangle me-2"></i>
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          <div className="row">
            <div className="col-lg-6 mb-4">
              <div className="card shadow">
                <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">
                    <i className="fas fa-plus-circle me-2"></i>
                    Submit Rating
                  </h5>
                  <button 
                    type="button" 
                    className="btn btn-sm btn-light"
                    onClick={resetForm}
                    disabled={submitting}
                  >
                    <i className="fas fa-redo me-1"></i>
                    Reset
                  </button>
                </div>
                <div className="card-body">
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      <label className="form-label fw-bold">Rating Type:</label>
                      <div className="btn-group w-100" role="group">
                        <button
                          type="button"
                          className={`btn ${selectedType === 'lecturer_rating' ? 'btn-primary' : 'btn-outline-primary'}`}
                          onClick={() => setSelectedType('lecturer_rating')}
                        >
                          <i className="fas fa-user me-1"></i>
                          Rate Lecturer
                        </button>
                        <button
                          type="button"
                          className={`btn ${selectedType === 'course_rating' ? 'btn-primary' : 'btn-outline-primary'}`}
                          onClick={() => setSelectedType('course_rating')}
                        >
                          <i className="fas fa-book me-1"></i>
                          Rate Course
                        </button>
                      </div>
                      <div className="form-text">
                        {selectedType === 'lecturer_rating' 
                          ? "You're rating the lecturer's teaching performance"
                          : "You're rating the course content and structure"
                        }
                      </div>
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Select Lecturer:</label>
                      <select
                        className="form-select"
                        value={selectedLecturerId}
                        onChange={(e) => setSelectedLecturerId(e.target.value)}
                        required
                        disabled={submitting}
                      >
                        <option value="">Choose a lecturer</option>
                        {lecturers.map(lecturer => (
                          <option key={lecturer.id} value={lecturer.id}>
                            {lecturer.first_name} {lecturer.last_name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Select Course:</label>
                      <select
                        className="form-select"
                        value={selectedCourseId}
                        onChange={(e) => setSelectedCourseId(e.target.value)}
                        required
                        disabled={submitting}
                      >
                        <option value="">Choose a course</option>
                        {courses.map(course => (
                          <option key={course.id} value={course.id}>
                            {course.course_code} - {course.course_name}
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Select Class:</label>
                      <select
                        className="form-select"
                        value={selectedClassName}
                        onChange={(e) => setSelectedClassName(e.target.value)}
                        required
                        disabled={submitting || !selectedLecturerId || !selectedCourseId}
                      >
                        <option value="">Choose a class</option>
                        {classes.map(classItem => (
                          <option key={classItem.id} value={classItem.class_name}>
                            {classItem.class_name}
                          </option>
                        ))}
                      </select>
                      {selectedLecturerId && selectedCourseId && selectedClassName && 
                       hasRated(selectedLecturerId, selectedCourseId, selectedClassName) && (
                        <div className="text-warning mt-2">
                          <i className="fas fa-exclamation-triangle me-1"></i>
                          <small>You have already rated this combination</small>
                        </div>
                      )}
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Rating:</label>
                      <div className="d-flex align-items-center mb-2">
                        {[1, 2, 3, 4, 5].map(star => (
                          <button
                            key={star}
                            type="button"
                            className="btn btn-link p-1"
                            onClick={() => setRating(star)}
                            disabled={submitting}
                          >
                            <i
                              className={`fas fa-star fa-2x ${
                                star <= rating ? 'text-warning' : 'text-muted'
                              }`}
                            ></i>
                          </button>
                        ))}
                        <span className="ms-3 fw-bold fs-5">{rating}/5</span>
                      </div>
                      <div className="text-muted small">
                        {rating === 1 && "Poor"}
                        {rating === 2 && "Fair"}
                        {rating === 3 && "Good"}
                        {rating === 4 && "Very Good"}
                        {rating === 5 && "Excellent"}
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="form-label fw-bold">Comment (optional):</label>
                      <textarea
                        className="form-control"
                        rows="3"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        placeholder="Share your experience, feedback, or suggestions..."
                        disabled={submitting}
                        maxLength={500}
                      ></textarea>
                      <div className="form-text text-end">
                        {comment.length}/500 characters
                      </div>
                    </div>

                    <button
                      type="submit"
                      className="btn btn-primary w-100 py-2"
                      disabled={
                        submitting || 
                        !selectedLecturerId || 
                        !selectedCourseId || 
                        !selectedClassName || 
                        hasRated(selectedLecturerId, selectedCourseId, selectedClassName)
                      }
                    >
                      {submitting ? (
                        <>
                          <div className="spinner-border spinner-border-sm me-2"></div>
                          Submitting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-star me-2"></i>
                          Submit Rating
                        </>
                      )}
                    </button>

                    {hasRated(selectedLecturerId, selectedCourseId, selectedClassName) && (
                      <div className="alert alert-warning mt-3 mb-0 py-2">
                        <i className="fas fa-info-circle me-2"></i>
                        You can only rate each lecturer-course-class combination once
                      </div>
                    )}
                  </form>
                </div>
              </div>
            </div>

            <div className="col-lg-6">
              <div className="card shadow">
                <div className="card-header bg-success text-white">
                  <h5 className="mb-0">
                    <i className="fas fa-history me-2"></i>
                    My Ratings ({myRatings.length})
                  </h5>
                </div>
                <div className="card-body">
                  {loading ? (
                    <div className="text-center py-4">
                      <div className="spinner-border text-primary"></div>
                      <p className="mt-2">Loading ratings...</p>
                    </div>
                  ) : myRatings.length === 0 ? (
                    <div className="text-center py-5">
                      <i className="fas fa-star fa-3x text-muted mb-3"></i>
                      <h5 className="text-muted">No ratings submitted yet</h5>
                      <p className="text-muted">Start by rating a lecturer and course combination</p>
                    </div>
                  ) : (
                    <div className="list-group list-group-flush">
                      {myRatings.map((ratingItem) => (
                        <div key={ratingItem.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div>
                              <h6 className="mb-1 text-primary">
                                {ratingItem.lecturer_name} - {ratingItem.course_name}
                              </h6>
                              <span className={`badge me-2 ${
                                ratingItem.rating_type === 'lecturer_rating' ? 'bg-info' : 'bg-warning'
                              }`}>
                                {ratingItem.rating_type === 'lecturer_rating' ? 'Lecturer Rating' : 'Course Rating'}
                              </span>
                              <span className="badge bg-secondary">
                                Class: {ratingItem.class_name}
                              </span>
                            </div>
                            <small className="text-muted">
                              {ratingItem.created_at ? new Date(ratingItem.created_at).toLocaleDateString() : 'Recent'}
                            </small>
                          </div>
                          
                          <div className="mb-2">
                            {[1, 2, 3, 4, 5].map(star => (
                              <i
                                key={star}
                                className={`fas fa-star ${
                                  star <= ratingItem.rating ? 'text-warning' : 'text-muted'
                                } me-1`}
                              ></i>
                            ))}
                            <span className="fw-bold ms-1">{ratingItem.rating}/5</span>
                          </div>
                          
                          {ratingItem.comment && (
                            <div className="bg-light p-2 rounded">
                              <p className="mb-0 small">
                                <i className="fas fa-comment text-muted me-1"></i>
                                "{ratingItem.comment}"
                              </p>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
