// Rating.jsx - Fixed with proper API endpoints and data handling
import React, { useEffect, useState } from 'react';
import { useAuth } from '../utils/auth';
import { api, API_ENDPOINTS } from '../utils/api';

export default function Rating() {
  const { user } = useAuth();
  const [myRatings, setMyRatings] = useState([]);
  const [receivedRatings, setReceivedRatings] = useState([]);
  const [courses, setCourses] = useState([]);
  const [lecturers, setLecturers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedItem, setSelectedItem] = useState(null);
  const [rating, setRating] = useState(0);
  const [comment, setComment] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');

      // Fetch data based on user role
      if (user?.role === 'Student') {
        const [myRatingsRes, coursesRes, lecturersRes] = await Promise.all([
          api.get(API_ENDPOINTS.RATINGS_MY, true),
          api.get(API_ENDPOINTS.COURSES, true),
          api.get(API_ENDPOINTS.LECTURERS, true)
        ]);

        if (myRatingsRes.success) setMyRatings(myRatingsRes.data || []);
        if (coursesRes.success) setCourses(coursesRes.data || []);
        if (lecturersRes.success) setLecturers(lecturersRes.data || []);

      } else if (user?.role === 'Lecturer') {
        const [receivedRes, myRatingsRes] = await Promise.all([
          api.get(API_ENDPOINTS.RATINGS_LECTURER, true),
          api.get(API_ENDPOINTS.RATINGS_MY, true)
        ]);

        if (receivedRes.success) setReceivedRatings(receivedRes.data || []);
        if (myRatingsRes.success) setMyRatings(myRatingsRes.data || []);

      } else {
        // PRL, PL, Admin - fetch all ratings
        const [allRatingsRes, coursesRes, lecturersRes] = await Promise.all([
          api.get(API_ENDPOINTS.RATINGS, true),
          api.get(API_ENDPOINTS.COURSES, true),
          api.get(API_ENDPOINTS.LECTURERS, true)
        ]);

        if (allRatingsRes.success) setMyRatings(allRatingsRes.data || []);
        if (coursesRes.success) setCourses(coursesRes.data || []);
        if (lecturersRes.success) setLecturers(lecturersRes.data || []);
      }

    } catch (error) {
      console.error('Failed to fetch rating data:', error);
      setError('Failed to load rating data. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const submitRating = async () => {
    try {
      setError('');
      
      const ratingData = {
        lecturer_id: selectedItem.type === 'lecturer' ? selectedItem.id : null,
        course_id: selectedItem.type === 'course' ? selectedItem.id : null,
        rating: parseInt(rating),
        comment: comment.trim() || null
      };

      // Validate rating data
      if (!ratingData.lecturer_id && !ratingData.course_id) {
        setError('Please select either a course or lecturer to rate.');
        return;
      }

      const response = await api.post(API_ENDPOINTS.RATINGS, ratingData, true);
      
      if (response.success) {
        setSelectedItem(null);
        setRating(0);
        setComment('');
        await fetchData(); // Refresh data
      } else {
        setError(response.message || 'Failed to submit rating.');
      }
    } catch (error) {
      console.error('Failed to submit rating:', error);
      setError(error.message || 'Failed to submit rating. Please try again.');
    }
  };

  const RatingStars = ({ currentRating, onRatingChange, readonly = false, size = '2x' }) => {
    return (
      <div className="rating-stars">
        {[1, 2, 3, 4, 5].map(star => (
          <button
            key={star}
            type="button"
            className={`btn btn-link p-1 ${star <= currentRating ? 'text-warning' : 'text-muted'} ${readonly ? 'pe-none' : ''}`}
            onClick={() => !readonly && onRatingChange(star)}
            disabled={readonly}
          >
            <i className={`fas fa-star fa-${size}`}></i>
          </button>
        ))}
      </div>
    );
  };

  // Check if user has already rated an item
  const hasRatedItem = (itemId, itemType) => {
    return myRatings.some(rating => 
      (itemType === 'course' && rating.course_id === itemId) ||
      (itemType === 'lecturer' && rating.lecturer_id === itemId)
    );
  };

  // Role-based configuration
  const getRoleConfig = () => {
    const config = {
      Student: {
        title: 'â­ Rate Your Learning Experience',
        description: 'Provide feedback on courses and lecturers to help improve teaching quality',
        canRate: true,
        viewType: 'student'
      },
      Lecturer: {
        title: 'ðŸ“Š Ratings & Feedback Received',
        description: 'View ratings and feedback from students for your courses',
        canRate: false,
        viewType: 'lecturer'
      },
      PRL: {
        title: 'ðŸ“ˆ Rating Analytics',
        description: 'Monitor rating trends and feedback across your faculty',
        canRate: false,
        viewType: 'admin'
      },
      PL: {
        title: 'ðŸ“ˆ Program Ratings',
        description: 'Overview of ratings and feedback in your program',
        canRate: false,
        viewType: 'admin'
      },
      Admin: {
        title: 'ðŸ“Š System Rating Analytics',
        description: 'Comprehensive overview of all ratings and feedback in the system',
        canRate: false,
        viewType: 'admin'
      }
    };
    return config[user?.role] || config.Student;
  };

  const roleConfig = getRoleConfig();

  if (loading) {
    return (
      <div className="container-fluid">
        <div className="d-flex justify-content-center align-items-center py-5">
          <div className="text-center">
            <div className="spinner-border text-primary mb-3" role="status">
              <span className="visually-hidden">Loading ratings...</span>
            </div>
            <p className="text-muted">Loading rating data...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container-fluid">
      <div className="row mb-4">
        <div className="col-12">
          <h4 className="text-primary mb-1 fw-bold">{roleConfig.title}</h4>
          <p className="text-muted">{roleConfig.description}</p>
        </div>
      </div>

      {error && (
        <div className="alert alert-danger d-flex align-items-center" role="alert">
          <i className="fas fa-exclamation-triangle me-2"></i>
          <div>{error}</div>
        </div>
      )}

      {/* Rating Modal for Students */}
      {selectedItem && roleConfig.canRate && (
        <div className="modal show d-block" style={{ backgroundColor: 'rgba(0,0,0,0.5)' }}>
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  Rate {selectedItem.type === 'course' ? 'Course' : 'Lecturer'}
                </h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => {
                    setSelectedItem(null);
                    setRating(0);
                    setComment('');
                  }}
                ></button>
              </div>
              <div className="modal-body">
                <div className="text-center mb-4">
                  <h6 className="text-primary">{selectedItem.name}</h6>
                  {selectedItem.additionalInfo && (
                    <p className="text-muted mb-0">{selectedItem.additionalInfo}</p>
                  )}
                </div>
                
                <div className="text-center mb-4">
                  <label className="form-label fw-semibold mb-3">Select Rating:</label>
                  <RatingStars currentRating={rating} onRatingChange={setRating} size="lg" />
                  {rating > 0 && (
                    <div className="mt-2">
                      <small className="text-muted">
                        {rating === 1 && 'Poor'}
                        {rating === 2 && 'Fair'}
                        {rating === 3 && 'Good'}
                        {rating === 4 && 'Very Good'}
                        {rating === 5 && 'Excellent'}
                      </small>
                    </div>
                  )}
                </div>

                <div className="mb-3">
                  <label className="form-label fw-semibold">Comments (Optional)</label>
                  <textarea
                    className="form-control"
                    rows="3"
                    value={comment}
                    onChange={(e) => setComment(e.target.value)}
                    placeholder="Share your experience, suggestions, or feedback..."
                  ></textarea>
                </div>

                <div className="d-flex gap-2 justify-content-end">
                  <button 
                    type="button" 
                    className="btn btn-secondary" 
                    onClick={() => {
                      setSelectedItem(null);
                      setRating(0);
                      setComment('');
                    }}
                  >
                    Cancel
                  </button>
                  <button 
                    type="button" 
                    className="btn btn-primary"
                    onClick={submitRating}
                    disabled={rating === 0}
                  >
                    <i className="fas fa-paper-plane me-2"></i>
                    Submit Rating
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Student Rating Interface */}
      {roleConfig.viewType === 'student' && (
        <div className="row">
          {/* Rate Courses */}
          <div className="col-md-6 mb-4">
            <div className="card h-100 border-0 shadow-sm">
              <div className="card-header bg-primary text-white">
                <h5 className="mb-0">
                  <i className="fas fa-book me-2"></i>
                  Rate Courses
                </h5>
              </div>
              <div className="card-body">
                <p className="text-muted mb-4">
                  Provide feedback on courses you've taken to help improve course content and delivery.
                </p>
                <div className="row g-2">
                  {courses.slice(0, 6).map(course => {
                    const alreadyRated = hasRatedItem(course.id, 'course');
                    return (
                      <div key={course.id} className="col-12">
                        <div className="card border">
                          <div className="card-body py-3">
                            <div className="d-flex justify-content-between align-items-center">
                              <div>
                                <h6 className="mb-1 text-primary">{course.course_code}</h6>
                                <p className="mb-0 text-muted small">{course.course_name}</p>
                              </div>
                              <button
                                className={`btn ${alreadyRated ? 'btn-outline-success' : 'btn-outline-primary'} btn-sm`}
                                onClick={() => setSelectedItem({
                                  id: course.id,
                                  type: 'course',
                                  name: course.course_name,
                                  additionalInfo: course.course_code
                                })}
                                disabled={alreadyRated}
                              >
                                {alreadyRated ? (
                                  <>
                                    <i className="fas fa-check me-1"></i>
                                    Rated
                                  </>
                                ) : (
                                  <>
                                    <i className="fas fa-star me-1"></i>
                                    Rate
                                  </>
                                )}
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
                {courses.length === 0 && (
                  <div className="text-center py-4">
                    <i className="fas fa-book fa-3x text-muted mb-3"></i>
                    <p className="text-muted">No courses available for rating.</p>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Rate Lecturers */}
          <div className="col-md-6 mb-4">
            <div className="card h-100 border-0 shadow-sm">
              <div className="card-header bg-success text-white">
                <h5 className="mb-0">
                  <i className="fas fa-chalkboard-teacher me-2"></i>
                  Rate Lecturers
                </h5>
              </div>
              <div className="card-body">
                <p className="text-muted mb-4">
                  Evaluate your lecturers' teaching quality, communication, and support.
                </p>
                <div className="row g-2">
                  {lecturers.slice(0, 6).map(lecturer => {
                    const alreadyRated = hasRatedItem(lecturer.id, 'lecturer');
                    return (
                      <div key={lecturer.id} className="col-12">
                        <div className="card border">
                          <div className="card-body py-3">
                            <div className="d-flex justify-content-between align-items-center">
                              <div>
                                <h6 className="mb-1 text-success">
                                  {lecturer.first_name} {lecturer.last_name}
                                </h6>
                                <p className="mb-0 text-muted small">{lecturer.email}</p>
                              </div>
                              <button
                                className={`btn ${alreadyRated ? 'btn-outline-success' : 'btn-outline-success'} btn-sm`}
                                onClick={() => setSelectedItem({
                                  id: lecturer.id,
                                  type: 'lecturer',
                                  name: `${lecturer.first_name} ${lecturer.last_name}`,
                                  additionalInfo: lecturer.email
                                })}
                                disabled={alreadyRated}
                              >
                                {alreadyRated ? (
                                  <>
                                    <i className="fas fa-check me-1"></i>
                                    Rated
                                  </>
                                ) : (
                                  <>
                                    <i className="fas fa-star me-1"></i>
                                    Rate
                                  </>
                                )}
                              </button>
                            </div>
                          </div>
                        </div>
                      </div>
                    );
                  })}
                </div>
                {lecturers.length === 0 && (
                  <div className="text-center py-4">
                    <i className="fas fa-users fa-3x text-muted mb-3"></i>
                    <p className="text-muted">No lecturers available for rating.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Lecturer Rating Display */}
      {roleConfig.viewType === 'lecturer' && (
        <div className="row">
          <div className="col-12">
            <div className="card border-0 shadow-sm">
              <div className="card-header bg-white">
                <h5 className="mb-0">
                  <i className="fas fa-chart-bar me-2 text-primary"></i>
                  Your Rating Summary
                </h5>
              </div>
              <div className="card-body">
                {receivedRatings.length > 0 ? (
                  <>
                    <div className="row mb-4 text-center">
                      <div className="col-md-3">
                        <div className="text-primary fw-bold fs-2">
                          {receivedRatings.length}
                        </div>
                        <small className="text-muted">Total Ratings</small>
                      </div>
                      <div className="col-md-3">
                        <div className="text-warning fw-bold fs-2">
                          {(receivedRatings.reduce((acc, curr) => acc + curr.rating, 0) / receivedRatings.length).toFixed(1)}
                        </div>
                        <small className="text-muted">Average Rating</small>
                      </div>
                      <div className="col-md-3">
                        <div className="text-success fw-bold fs-2">
                          {receivedRatings.filter(r => r.rating >= 4).length}
                        </div>
                        <small className="text-muted">Positive Ratings (4+ stars)</small>
                      </div>
                      <div className="col-md-3">
                        <div className="text-info fw-bold fs-2">
                          {new Set(receivedRatings.map(r => r.course_id)).size}
                        </div>
                        <small className="text-muted">Courses Rated</small>
                      </div>
                    </div>

                    <div className="table-responsive">
                      <table className="table table-hover">
                        <thead className="table-light">
                          <tr>
                            <th>Course</th>
                            <th>Rating</th>
                            <th>Comment</th>
                            <th>Date</th>
                          </tr>
                        </thead>
                        <tbody>
                          {receivedRatings.map(ratingItem => (
                            <tr key={ratingItem.id}>
                              <td>
                                <strong>{ratingItem.course_name || 'N/A'}</strong>
                                <br />
                                <small className="text-muted">{ratingItem.course_code || 'No course code'}</small>
                              </td>
                              <td>
                                <div className="d-flex align-items-center">
                                  <RatingStars currentRating={ratingItem.rating} onRatingChange={() => {}} readonly />
                                  <span className="ms-2 fw-bold text-warning">
                                    {ratingItem.rating}/5
                                  </span>
                                </div>
                              </td>
                              <td>
                                {ratingItem.comment ? (
                                  <div>
                                    <p className="mb-0">{ratingItem.comment}</p>
                                    {ratingItem.student_name && (
                                      <small className="text-muted">- {ratingItem.student_name}</small>
                                    )}
                                  </div>
                                ) : (
                                  <span className="text-muted">No comment</span>
                                )}
                              </td>
                              <td>
                                {new Date(ratingItem.created_at).toLocaleDateString()}
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </>
                ) : (
                  <div className="text-center py-5">
                    <i className="fas fa-star fa-4x text-muted mb-3"></i>
                    <h5 className="text-muted">No Ratings Received Yet</h5>
                    <p className="text-muted">You haven't received any ratings from students yet.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Admin/PRL/PL Rating Analytics */}
      {roleConfig.viewType === 'admin' && (
        <div className="row">
          <div className="col-12">
            <div className="card border-0 shadow-sm">
              <div className="card-header bg-white">
                <h5 className="mb-0">
                  <i className="fas fa-chart-line me-2 text-success"></i>
                  Rating Analytics Overview
                </h5>
              </div>
              <div className="card-body">
                <div className="alert alert-info">
                  <i className="fas fa-info-circle me-2"></i>
                  <strong>Analytics Dashboard:</strong> {roleConfig.description}
                </div>
                
                {myRatings.length > 0 ? (
                  <>
                    <div className="row text-center mb-4">
                      <div className="col-md-2">
                        <div className="card border-0 bg-light">
                          <div className="card-body py-3">
                            <div className="text-primary fw-bold fs-3">{myRatings.length}</div>
                            <small className="text-muted">Total Ratings</small>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="card border-0 bg-light">
                          <div className="card-body py-3">
                            <div className="text-warning fw-bold fs-3">
                              {(myRatings.reduce((acc, curr) => acc + curr.rating, 0) / myRatings.length).toFixed(1)}
                            </div>
                            <small className="text-muted">Avg Rating</small>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="card border-0 bg-light">
                          <div className="card-body py-3">
                            <div className="text-success fw-bold fs-3">
                              {new Set(myRatings.map(r => r.course_id)).size}
                            </div>
                            <small className="text-muted">Courses Rated</small>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="card border-0 bg-light">
                          <div className="card-body py-3">
                            <div className="text-info fw-bold fs-3">
                              {new Set(myRatings.map(r => r.lecturer_id)).size}
                            </div>
                            <small className="text-muted">Lecturers Rated</small>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="card border-0 bg-light">
                          <div className="card-body py-3">
                            <div className="text-danger fw-bold fs-3">
                              {Math.round((myRatings.filter(r => r.rating >= 4).length / myRatings.length) * 100)}%
                            </div>
                            <small className="text-muted">Satisfaction</small>
                          </div>
                        </div>
                      </div>
                      <div className="col-md-2">
                        <div className="card border-0 bg-light">
                          <div className="card-body py-3">
                            <div className="text-secondary fw-bold fs-3">
                              {new Set(myRatings.map(r => r.user_id)).size}
                            </div>
                            <small className="text-muted">Active Raters</small>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="table-responsive">
                      <table className="table table-hover">
                        <thead className="table-light">
                          <tr>
                            <th>Rated Item</th>
                            <th>Type</th>
                            <th>Rating</th>
                            <th>Comment</th>
                            <th>Rated By</th>
                            <th>Date</th>
                          </tr>
                        </thead>
                        <tbody>
                          {myRatings.slice(0, 10).map(ratingItem => (
                            <tr key={ratingItem.id}>
                              <td>
                                {ratingItem.course_name || ratingItem.lecturer_name || 'N/A'}
                                <br />
                                <small className="text-muted">
                                  {ratingItem.course_code || 'Lecturer Rating'}
                                </small>
                              </td>
                              <td>
                                <span className={`badge ${ratingItem.course_name ? 'bg-primary' : 'bg-success'}`}>
                                  {ratingItem.course_name ? 'Course' : 'Lecturer'}
                                </span>
                              </td>
                              <td>
                                <div className="d-flex align-items-center">
                                  <RatingStars currentRating={ratingItem.rating} onRatingChange={() => {}} readonly size="sm" />
                                  <span className="ms-2 fw-bold text-warning">
                                    {ratingItem.rating}/5
                                  </span>
                                </div>
                              </td>
                              <td>
                                {ratingItem.comment || <span className="text-muted">No comment</span>}
                              </td>
                              <td>
                                {ratingItem.student_name || 'Unknown'}
                              </td>
                              <td>
                                {new Date(ratingItem.created_at).toLocaleDateString()}
                              </td>
                            </tr>
                          ))}
                        </tbody>
                      </table>
                    </div>
                  </>
                ) : (
                  <div className="text-center py-5">
                    <i className="fas fa-chart-bar fa-3x text-muted mb-3"></i>
                    <h5 className="text-muted">No Rating Data Available</h5>
                    <p className="text-muted">There are no ratings in the system yet.</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      )}

      {/* Rating History for Students and Lecturers */}
      {(myRatings.length > 0 && roleConfig.viewType !== 'admin') && (
        <div className="card mt-4 border-0 shadow-sm">
          <div className="card-header bg-white">
            <h5 className="mb-0">
              <i className="fas fa-history me-2 text-primary"></i>
              Your Rating History
            </h5>
          </div>
          <div className="card-body">
            <div className="table-responsive">
              <table className="table table-hover">
                <thead className="table-light">
                  <tr>
                    <th>Rated Item</th>
                    <th>Type</th>
                    <th>Your Rating</th>
                    <th>Your Comment</th>
                    <th>Date</th>
                  </tr>
                </thead>
                <tbody>
                  {myRatings.map(ratingItem => (
                    <tr key={ratingItem.id}>
                      <td>
                        <strong>
                          {ratingItem.course_name || ratingItem.lecturer_name}
                        </strong>
                        <br />
                        <small className="text-muted">
                          {ratingItem.course_code || 'Lecturer Rating'}
                        </small>
                      </td>
                      <td>
                        <span className={`badge ${ratingItem.course_name ? 'bg-primary' : 'bg-success'}`}>
                          {ratingItem.course_name ? 'Course' : 'Lecturer'}
                        </span>
                      </td>
                      <td>
                        <div className="d-flex align-items-center">
                          <RatingStars currentRating={ratingItem.rating} onRatingChange={() => {}} readonly size="sm" />
                          <span className="ms-2 fw-bold text-warning">
                            {ratingItem.rating}/5
                          </span>
                        </div>
                      </td>
                      <td>
                        {ratingItem.comment || <span className="text-muted">No comment</span>}
                      </td>
                      <td>
                        {new Date(ratingItem.created_at).toLocaleDateString()}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>
        </div>
      )}
    </div>
  );
}
