import React, { useEffect, useState } from "react";
import axios from "axios";
import API_BASE_URL from "../utils/api";

const Reports = () => {
  const [reports, setReports] = useState([]);
  const [stats, setStats] = useState({});
  const token = localStorage.getItem("token");
  const authHeaders = { headers: { Authorization: `Bearer ${token}` } };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [repRes, statRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/data/reports`, authHeaders),
          axios.get(`${API_BASE_URL}/api/data/reports/stats`, authHeaders),
        ]);
        setReports(repRes.data.data.reports || []);
        setStats(statRes.data.data.overview || {});
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, []);

  return (
    <div className="reports">
      <h2>Reports</h2>
      <p>Total Reports: {stats.total_reports || 0}</p>
      <ul>
        {reports.map((r) => (
          <li key={r.id}>
            {r.course_name} - Week {r.week_of_reporting} - Present: {r.actual_present}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Reports;
