import React, { useState, useEffect } from "react";
import axios from "axios";
import API_BASE_URL from "../utils/api";

const ReportForm = () => {
  const [form, setForm] = useState({});
  const [faculties, setFaculties] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classes, setClasses] = useState([]);

  const token = localStorage.getItem("token");
  const authHeaders = { headers: { Authorization: `Bearer ${token}` } };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const [facRes, courseRes, classRes] = await Promise.all([
          axios.get(`${API_BASE_URL}/api/data/faculties`, authHeaders),
          axios.get(`${API_BASE_URL}/api/data/courses`, authHeaders),
          axios.get(`${API_BASE_URL}/api/data/classes`, authHeaders),
        ]);
        setFaculties(facRes.data.data);
        setCourses(courseRes.data.data);
        setClasses(classRes.data.data);
      } catch (err) {
        console.error(err);
      }
    };
    fetchData();
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      await axios.post(`${API_BASE_URL}/api/data/reports`, form, authHeaders);
      alert("Report submitted successfully");
    } catch (err) {
      alert(err.response?.data?.message || "Report submission failed");
    }
  };

  return (
    <div className="report-form">
      <h2>Submit Report</h2>
      <form onSubmit={handleSubmit}>
        <select name="faculty_id" onChange={handleChange} required>
          <option value="">Select Faculty</option>
          {faculties.map((f) => (
            <option key={f.id} value={f.id}>{f.name}</option>
          ))}
        </select>
        <select name="course_id" onChange={handleChange} required>
          <option value="">Select Course</option>
          {courses.map((c) => (
            <option key={c.id} value={c.id}>{c.course_name}</option>
          ))}
        </select>
        <select name="class_id" onChange={handleChange} required>
          <option value="">Select Class</option>
          {classes.map((cl) => (
            <option key={cl.id} value={cl.id}>{cl.class_name}</option>
          ))}
        </select>
        <input type="number" name="week_of_reporting" placeholder="Week" onChange={handleChange} required />
        <input type="date" name="lecture_date" onChange={handleChange} required />
        <input type="number" name="actual_present" placeholder="Number Present" onChange={handleChange} required />
        <button type="submit">Submit</button>
      </form>
    </div>
  );
};

export default ReportForm;
