import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/auth';
import { apiMethods } from '../utils/api';

export default function Rating() {
  const [classes, setClasses] = useState([]);
  const [myRatings, setMyRatings] = useState([]);
  const [selectedClassName, setSelectedClassName] = useState('');
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const { user } = useAuth();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const classesRes = await apiMethods.getClasses();

      if (classesRes.success) setClasses(classesRes.data || []);

      // Try to get ratings, but don't fail if it errors
      try {
        const ratingsRes = user?.role === 'Lecturer' 
          ? await apiMethods.getLecturerRatings()
          : await apiMethods.getMyRatings();
        
        if (ratingsRes.success) setMyRatings(ratingsRes.data || []);
      } catch (ratingsError) {
        console.warn('Could not load ratings:', ratingsError);
        setMyRatings([]);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load some data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!selectedClassName) {
      setError('Please select a class');
      return;
    }

    if (rating < 1 || rating > 5) {
      setError('Please select a valid rating between 1 and 5');
      return;
    }

    setSubmitting(true);
    setError('');
    setSuccess('');

    try {
      // Find the selected class to get lecturer_id and course_id
      const selectedClass = classes.find(cls => cls.class_name === selectedClassName);
      
      if (!selectedClass) {
        setError('Selected class not found');
        setSubmitting(false);
        return;
      }

      // Try different rating_type values - let's start with 'class_rating'
      const ratingData = {
        rating: Number(rating),
        comment: comment.trim(),
        class_name: selectedClassName,
        rating_type: 'class_rating', // Try this first
        lecturer_id: selectedClass.lecturer_id, // Get from the class
        course_id: selectedClass.course_id // Get from the class
      };

      console.log('Submitting rating data:', ratingData);

      const response = await fetch('https://luct-7.onrender.com/api/ratings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${localStorage.getItem('token')}`
        },
        body: JSON.stringify(ratingData)
      });

      const responseData = await response.json();
      console.log('Backend response:', responseData);

      if (response.ok) {
        setSuccess('Rating submitted successfully!');
        setRating(5);
        setComment('');
        setSelectedClassName('');
        fetchData(); // Refresh ratings
      } else {
        // If 'class_rating' fails, try other values
        if (responseData.message.includes('rating type')) {
          setError('Invalid rating type. Please try again with a different type.');
        } else {
          setError(responseData.message || `Error: ${response.status} ${response.statusText}`);
        }
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      setError(error.message || 'Failed to submit rating. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  // Function to try different rating types
  const tryDifferentRatingType = async (selectedClass, ratingType) => {
    const ratingData = {
      rating: Number(rating),
      comment: comment.trim(),
      class_name: selectedClassName,
      rating_type: ratingType,
      lecturer_id: selectedClass.lecturer_id,
      course_id: selectedClass.course_id
    };

    console.log(`Trying rating type: ${ratingType}`, ratingData);

    const response = await fetch('https://luct-7.onrender.com/api/ratings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${localStorage.getItem('token')}`
      },
      body: JSON.stringify(ratingData)
    });

    return response;
  };

  const hasRated = (className) => {
    return myRatings.some(rating => rating.class_name === className);
  };

  const resetForm = () => {
    setSelectedClassName('');
    setRating(5);
    setComment('');
    setError('');
    setSuccess('');
  };

  // Get class display name with lecturer and course info
  const getClassDisplayName = (classItem) => {
    return `${classItem.class_name} (${classItem.course_name} - ${classItem.lecturer_name})`;
  };

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <h2 className="text-primary mb-4">
            <i className="fas fa-star me-2"></i>
            Class Ratings
          </h2>

          {/* Success Message */}
          {success && (
            <div className="alert alert-success alert-dismissible fade show" role="alert">
              <i className="fas fa-check-circle me-2"></i>
              {success}
              <button type="button" className="btn-close" onClick={() => setSuccess('')}></button>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="alert alert-danger alert-dismissible fade show" role="alert">
              <i className="fas fa-exclamation-triangle me-2"></i>
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          <div className="row">
            <div className="col-lg-6 mb-4">
              <div className="card shadow">
                <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">
                    <i className="fas fa-plus-circle me-2"></i>
                    Rate a Class
                  </h5>
                  <button 
                    type="button" 
                    className="btn btn-sm btn-light"
                    onClick={resetForm}
                    disabled={submitting}
                  >
                    <i className="fas fa-redo me-1"></i>
                    Reset
                  </button>
                </div>
                <div className="card-body">
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      <label className="form-label fw-bold">Select Class:</label>
                      <select
                        className="form-select"
                        value={selectedClassName}
                        onChange={(e) => setSelectedClassName(e.target.value)}
                        required
                        disabled={submitting}
                      >
                        <option value="">Choose a class to rate</option>
                        {classes.map(classItem => (
                          <option key={classItem.id} value={classItem.class_name}>
                            {getClassDisplayName(classItem)}
                          </option>
                        ))}
                      </select>
                      {selectedClassName && hasRated(selectedClassName) && (
                        <div className="text-warning mt-2">
                          <i className="fas fa-exclamation-triangle me-1"></i>
                          <small>You have already rated this class</small>
                        </div>
                      )}
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Rating:</label>
                      <div className="d-flex align-items-center mb-2">
                        {[1, 2, 3, 4, 5].map(star => (
                          <button
                            key={star}
                            type="button"
                            className="btn btn-link p-1"
                            onClick={() => setRating(star)}
                            disabled={submitting}
                          >
                            <i
                              className={`fas fa-star fa-2x ${
                                star <= rating ? 'text-warning' : 'text-muted'
                              }`}
                            ></i>
                          </button>
                        ))}
                        <span className="ms-3 fw-bold fs-5">{rating}/5</span>
                      </div>
                      <div className="text-muted small">
                        {rating === 1 && "Poor"}
                        {rating === 2 && "Fair"}
                        {rating === 3 && "Good"}
                        {rating === 4 && "Very Good"}
                        {rating === 5 && "Excellent"}
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="form-label fw-bold">Comment (optional):</label>
                      <textarea
                        className="form-control"
                        rows="3"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        placeholder="Share your experience with this class..."
                        disabled={submitting}
                        maxLength={500}
                      ></textarea>
                      <div className="form-text text-end">
                        {comment.length}/500 characters
                      </div>
                    </div>

                    <button
                      type="submit"
                      className="btn btn-primary w-100 py-2"
                      disabled={
                        submitting || 
                        !selectedClassName || 
                        hasRated(selectedClassName)
                      }
                    >
                      {submitting ? (
                        <>
                          <div className="spinner-border spinner-border-sm me-2"></div>
                          Submitting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-star me-2"></i>
                          Submit Rating
                        </>
                      )}
                    </button>

                    {hasRated(selectedClassName) && (
                      <div className="alert alert-warning mt-3 mb-0 py-2">
                        <i className="fas fa-info-circle me-2"></i>
                        You can only rate each class once
                      </div>
                    )}
                  </form>
                </div>
              </div>
            </div>

            <div className="col-lg-6">
              <div className="card shadow">
                <div className="card-header bg-success text-white">
                  <h5 className="mb-0">
                    <i className="fas fa-history me-2"></i>
                    My Class Ratings ({myRatings.length})
                  </h5>
                </div>
                <div className="card-body">
                  {loading ? (
                    <div className="text-center py-4">
                      <div className="spinner-border text-primary"></div>
                      <p className="mt-2">Loading ratings...</p>
                    </div>
                  ) : myRatings.length === 0 ? (
                    <div className="text-center py-5">
                      <i className="fas fa-star fa-3x text-muted mb-3"></i>
                      <h5 className="text-muted">No ratings submitted yet</h5>
                      <p className="text-muted">Start by rating one of your classes</p>
                    </div>
                  ) : (
                    <div className="list-group list-group-flush">
                      {myRatings.map((ratingItem) => (
                        <div key={ratingItem.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div>
                              <h6 className="mb-1 text-primary">
                                {ratingItem.class_name}
                              </h6>
                              <div className="small text-muted mb-1">
                                {ratingItem.course_name} - {ratingItem.lecturer_name}
                              </div>
                              <span className="badge bg-info me-2">
                                {ratingItem.rating_type || 'Class Rating'}
                              </span>
                            </div>
                            <small className="text-muted">
                              {ratingItem.created_at ? new Date(ratingItem.created_at).toLocaleDateString() : 'Recent'}
                            </small>
                          </div>
                          
                          <div className="mb-2">
                            {[1, 2, 3, 4, 5].map(star => (
                              <i
                                key={star}
                                className={`fas fa-star ${
                                  star <= ratingItem.rating ? 'text-warning' : 'text-muted'
                                } me-1`}
                              ></i>
                            ))}
                            <span className="fw-bold ms-1">{ratingItem.rating}/5</span>
                          </div>
                          
                          {ratingItem.comment && (
                            <div className="bg-light p-2 rounded">
                              <p className="mb-0 small">
                                <i className="fas fa-comment text-muted me-1"></i>
                                "{ratingItem.comment}"
                              </p>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>

          {/* Debug section - try different rating types */}
          {error && error.includes('rating type') && (
            <div className="card border-warning mt-4">
              <div className="card-header bg-warning text-dark">
                <h6 className="mb-0">
                  <i className="fas fa-bug me-2"></i>
                  Debug: Try Different Rating Types
                </h6>
              </div>
              <div className="card-body">
                <p className="small text-muted mb-3">
                  The backend is rejecting the rating type. Try these alternatives:
                </p>
                <div className="btn-group">
                  {['class', 'lecturer', 'course', 'general', 'overall', 'teaching', 'content'].map(type => (
                    <button
                      key={type}
                      type="button"
                      className="btn btn-outline-primary btn-sm"
                      onClick={async () => {
                        const selectedClass = classes.find(cls => cls.class_name === selectedClassName);
                        if (selectedClass) {
                          setSubmitting(true);
                          const response = await tryDifferentRatingType(selectedClass, type);
                          const responseData = await response.json();
                          console.log(`Tried ${type}:`, responseData);
                          if (response.ok) {
                            setSuccess(`Rating submitted successfully with type: ${type}`);
                            setSelectedClassName('');
                            fetchData();
                          } else {
                            setError(`Failed with type '${type}': ${responseData.message}`);
                          }
                          setSubmitting(false);
                        }
                      }}
                      disabled={submitting || !selectedClassName}
                    >
                      Try "{type}"
                    </button>
                  ))}
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}
