import React from 'react';
import { BrowserRouter, Routes, Route, Link, useLocation, Navigate } from 'react-router-dom';
import Home from './pages/Home';
import Login from './pages/Login';
import Register from './pages/Register';
import ReportForm from './pages/ReportForm';
import Reports from './pages/Reports';
import { AuthProvider, useAuth } from './utils/auth';

// Navigation Component
function Navigation() {
  const { user, logout, isAuthenticated } = useAuth();
  const location = useLocation();

  const handleLogout = () => {
    logout();
    // Redirect to home after logout
    window.location.href = '/';
  };

  const isActiveRoute = (path) => {
    return location.pathname === path ? 'active' : '';
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-primary shadow-sm">
      <div className="container">
        {/* Brand */}
        <Link className="navbar-brand fw-bold d-flex align-items-center" to="/">
          <i className="fas fa-university me-2"></i>
          LUCT Reports
        </Link>

        {/* Mobile Toggle */}
        <button
          className="navbar-toggler"
          type="button"
          data-bs-toggle="collapse"
          data-bs-target="#navbarNav"
          aria-controls="navbarNav"
          aria-expanded="false"
          aria-label="Toggle navigation"
        >
          <span className="navbar-toggler-icon"></span>
        </button>

        {/* Navigation Items */}
        <div className="collapse navbar-collapse" id="navbarNav">
          <ul className="navbar-nav me-auto">
            <li className="nav-item">
              <Link 
                className={`nav-link ${isActiveRoute('/')}`} 
                to="/"
              >
                <i className="fas fa-home me-1"></i>
                Home
              </Link>
            </li>

            {/* Show these links only when user is authenticated */}
            {isAuthenticated && (
              <>
                <li className="nav-item">
                  <Link 
                    className={`nav-link ${isActiveRoute('/report')}`} 
                    to="/report"
                  >
                    <i className="fas fa-plus-circle me-1"></i>
                    New Report
                  </Link>
                </li>
                <li className="nav-item">
                  <Link 
                    className={`nav-link ${isActiveRoute('/reports')}`} 
                    to="/reports"
                  >
                    <i className="fas fa-clipboard-list me-1"></i>
                    My Reports
                  </Link>
                </li>
              </>
            )}
          </ul>

          {/* User Section */}
          <ul className="navbar-nav">
            {isAuthenticated ? (
              <li className="nav-item dropdown">
                <a
                  className="nav-link dropdown-toggle d-flex align-items-center"
                  href="#"
                  id="navbarDropdown"
                  role="button"
                  data-bs-toggle="dropdown"
                  aria-expanded="false"
                >
                  <i className="fas fa-user-circle me-2"></i>
                  <span className="d-none d-md-inline">
                    {user?.first_name} {user?.last_name}
                  </span>
                </a>
                <ul className="dropdown-menu dropdown-menu-end" aria-labelledby="navbarDropdown">
                  <li>
                    <span className="dropdown-item-text">
                      <small className="text-muted">Signed in as</small>
                      <br />
                      <strong className="text-capitalize">{user?.role}</strong>
                    </span>
                  </li>
                  <li><hr className="dropdown-divider" /></li>
                  <li>
                    <Link className="dropdown-item" to="/reports">
                      <i className="fas fa-chart-bar me-2"></i>
                      My Reports
                    </Link>
                  </li>
                  <li>
                    <Link className="dropdown-item" to="/report">
                      <i className="fas fa-plus me-2"></i>
                      New Report
                    </Link>
                  </li>
                  <li><hr className="dropdown-divider" /></li>
                  <li>
                    <button 
                      className="dropdown-item text-danger" 
                      onClick={handleLogout}
                    >
                      <i className="fas fa-sign-out-alt me-2"></i>
                      Logout
                    </button>
                  </li>
                </ul>
              </li>
            ) : (
              /* Show login/register only when NOT authenticated */
              <>
                <li className="nav-item">
                  <Link 
                    className={`nav-link ${isActiveRoute('/login')}`} 
                    to="/login"
                  >
                    <i className="fas fa-sign-in-alt me-1"></i>
                    Login
                  </Link>
                </li>
                <li className="nav-item">
                  <Link 
                    className={`nav-link ${isActiveRoute('/register')}`} 
                    to="/register"
                  >
                    <i className="fas fa-user-plus me-1"></i>
                    Register
                  </Link>
                </li>
              </>
            )}
          </ul>
        </div>
      </div>
    </nav>
  );
}

// Private Route Component - Protects routes that require authentication
function PrivateRoute({ children }) {
  const { user, loading } = useAuth();
  const location = useLocation();

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
        <span className="ms-2 text-muted">Checking authentication...</span>
      </div>
    );
  }

  if (!user) {
    // Redirect to login page with return url so user comes back after login
    return <Navigate to="/login" state={{ from: location }} replace />;
  }

  return children;
}

// Public Route Component - Redirects authenticated users away from login/register
function PublicRoute({ children }) {
  const { user, loading } = useAuth();

  if (loading) {
    return (
      <div className="d-flex justify-content-center align-items-center" style={{ height: '50vh' }}>
        <div className="spinner-border text-primary" role="status">
          <span className="visually-hidden">Loading...</span>
        </div>
      </div>
    );
  }

  if (user) {
    // If user is already authenticated, redirect to home
    return <Navigate to="/" replace />;
  }

  return children;
}

// 404 Component
function NotFound() {
  return (
    <div className="container py-5">
      <div className="row justify-content-center">
        <div className="col-md-6 text-center">
          <div className="card shadow border-0">
            <div className="card-body py-5">
              <i className="fas fa-exclamation-triangle fa-4x text-warning mb-4"></i>
              <h2 className="text-primary mb-3">Page Not Found</h2>
              <p className="text-muted mb-4">
                The page you're looking for doesn't exist or has been moved.
              </p>
              <Link to="/" className="btn btn-primary">
                <i className="fas fa-home me-2"></i>
                Back to Home
              </Link>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

// Footer Component
function Footer() {
  return (
    <footer className="bg-dark text-white mt-5">
      <div className="container py-4">
        <div className="row">
          <div className="col-md-6">
            <h5 className="fw-bold">
              <i className="fas fa-university me-2"></i>
              LUCT Reporting System
            </h5>
            <p className="text-light mb-0">
              Streamlining academic reporting and management for Limkokwing University.
            </p>
          </div>
          <div className="col-md-6 text-md-end">
            <p className="text-light mb-0">
              &copy; 2024 Limkokwing University of Creative Technology. All rights reserved.
            </p>
            <small className="text-muted">
              Secure Academic Reporting Platform
            </small>
          </div>
        </div>
      </div>
    </footer>
  );
}

// Main App Content
function AppContent() {
  return (
    <div className="min-vh-100 bg-light">
      <Navigation />

      <main className="container-fluid px-0">
        <Routes>
          {/* Public Route - Accessible to all */}
          <Route path="/" element={<Home />} />

          {/* Public Routes - Only accessible when NOT logged in */}
          <Route 
            path="/login" 
            element={
              <PublicRoute>
                <Login />
              </PublicRoute>
            } 
          />
          <Route 
            path="/register" 
            element={
              <PublicRoute>
                <Register />
              </PublicRoute>
            } 
          />

          {/* Protected Routes - Only accessible when logged in */}
          <Route
            path="/report"
            element={
              <PrivateRoute>
                <ReportForm />
              </PrivateRoute>
            }
          />
          <Route
            path="/reports"
            element={
              <PrivateRoute>
                <Reports />
              </PrivateRoute>
            }
          />

          {/* Catch all route - 404 */}
          <Route path="*" element={<NotFound />} />
        </Routes>
      </main>

      <Footer />
    </div>
  );
}

// Main App Export
export default function App() {
  return (
    <AuthProvider>
      <BrowserRouter>
        <AppContent />
      </BrowserRouter>
    </AuthProvider>
  );
}
