import axios from "axios"

const API_BASE_URL = process.env.REACT_APP_API_URL || "https://luct-tcm7.onrender.com/api"

export const API_ENDPOINTS = {
  AUTH: {
    LOGIN: `${API_BASE_URL}/auth/login`,
    REGISTER: `${API_BASE_URL}/auth/register`,
    ME: `${API_BASE_URL}/auth/me`
  },
  REPORTS: {
    LIST: `${API_BASE_URL}/reports`,
    STATS: `${API_BASE_URL}/reports/stats`,
    CREATE: `${API_BASE_URL}/reports`
  },
  COURSES: `${API_BASE_URL}/courses`,
  CLASSES: `${API_BASE_URL}/classes`,
  SEARCH: `${API_BASE_URL}/search`,
  HEALTH: `${API_BASE_URL}/health`
}

const getAuthToken = () => localStorage.getItem('token')

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 30000,
})

api.interceptors.request.use(
  (config) => {
    const token = getAuthToken()
    if (token) {
      config.headers.Authorization = `Bearer ${token}`
    }
    return config
  },
  (error) => Promise.reject(error)
)

export const apiMethods = {
  async get(endpoint) {
    try {
      const response = await api.get(endpoint)
      return { success: true, data: response.data }
    } catch (error) {
      return { 
        success: false, 
        message: error.response?.data?.message || error.message 
      }
    }
  },

  async post(endpoint, data) {
    try {
      const response = await api.post(endpoint, data)
      return { success: true, data: response.data }
    } catch (error) {
      return { 
        success: false, 
        message: error.response?.data?.message || error.message 
      }
    }
  },

  async put(endpoint, data) {
    try {
      const response = await api.put(endpoint, data)
      return { success: true, data: response.data }
    } catch (error) {
      return { 
        success: false, 
        message: error.response?.data?.message || error.message 
      }
    }
  },

  async delete(endpoint) {
    try {
      const response = await api.delete(endpoint)
      return { success: true, data: response.data }
    } catch (error) {
      return { 
        success: false, 
        message: error.response?.data?.message || error.message 
      }
    }
  },

  // Convenience methods
  getProfile: function() { return this.get(API_ENDPOINTS.AUTH.ME) },
  getReports: function(params = {}) { return this.get(API_ENDPOINTS.REPORTS.LIST) },
  getReportStats: function() { return this.get(API_ENDPOINTS.REPORTS.STATS) },
  createReport: function(data) { return this.post(API_ENDPOINTS.REPORTS.CREATE, data) },
  getCourses: function() { return this.get(API_ENDPOINTS.COURSES) },
  getClasses: function() { return this.get(API_ENDPOINTS.CLASSES) },
  search: function(query) { return this.get(API_ENDPOINTS.SEARCH, { params: { q: query } }) },
  healthCheck: function() { return this.get(API_ENDPOINTS.HEALTH) }
}
