import React, { useEffect, useState } from "react";
import { API_ENDPOINTS, apiCall, createAuthHeaders } from "../utils/api";

const Home = () => {
  const [stats, setStats] = useState(null);
  const [error, setError] = useState("");
  const token = localStorage.getItem("token");

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const data = await apiCall(API_ENDPOINTS.REPORTS_STATS, {
          ...createAuthHeaders(token),
        });
        setStats(data.data);
      } catch (err) {
        setError("Failed to fetch statistics");
      }
    };
    if (token) fetchStats();
  }, [token]);

  return (
    <div className="page-container">
      <h2>Dashboard</h2>
      {error && <p className="error">{error}</p>}

      {stats ? (
        <div className="stats-grid">
          <div className="stat-card">
            <h3>Total Reports</h3>
            <p>{stats.overview?.total_reports}</p>
          </div>
          <div className="stat-card">
            <h3>Total Attendance</h3>
            <p>{stats.overview?.total_attendance}</p>
          </div>
          <div className="stat-card">
            <h3>Average Attendance</h3>
            <p>{Math.round(stats.overview?.avg_attendance || 0)}</p>
          </div>
          <div className="stat-card">
            <h3>Active Lecturers</h3>
            <p>{stats.overview?.active_lecturers}</p>
          </div>
          <div className="stat-card">
            <h3>Courses Covered</h3>
            <p>{stats.overview?.courses_covered}</p>
          </div>
        </div>
      ) : (
        <p>Loading statistics...</p>
      )}
    </div>
  );
};

export default Home;
