import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/auth';
import { apiMethods } from '../utils/api';

export default function Rating() {
  const [lecturers, setLecturers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [myRatings, setMyRatings] = useState([]);
  const [selectedType, setSelectedType] = useState('lecturer');
  const [selectedId, setSelectedId] = useState('');
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [ratingType, setRatingType] = useState('teaching');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const { user } = useAuth();

  // Valid rating types for your backend
  const ratingTypes = [
    { value: 'teaching', label: 'Teaching Quality' },
    { value: 'subject_knowledge', label: 'Subject Knowledge' },
    { value: 'communication', label: 'Communication' },
    { value: 'punctuality', label: 'Punctuality' },
    { value: 'overall', label: 'Overall' }
  ];

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      setLoading(true);
      setError('');

      // Fetch all necessary data
      const [lecturersRes, coursesRes] = await Promise.all([
        apiMethods.getLecturers(),
        apiMethods.getCourses()
      ]);

      if (lecturersRes.success) setLecturers(lecturersRes.data || []);
      if (coursesRes.success) setCourses(coursesRes.data || []);

      // Fetch ratings based on user role
      try {
        let ratingsRes;
        if (user?.role === 'Lecturer') {
          ratingsRes = await apiMethods.getLecturerRatings();
        } else if (user?.role === 'Student') {
          ratingsRes = await apiMethods.getMyRatings();
        } else {
          ratingsRes = { success: true, data: [] };
        }
        
        if (ratingsRes.success) {
          setMyRatings(ratingsRes.data || []);
        }
      } catch (ratingsError) {
        console.warn('Could not load ratings:', ratingsError);
        setMyRatings([]);
      }

    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!selectedId) {
      setError(`Please select a ${selectedType}`);
      return;
    }

    if (!rating) {
      setError('Please select a rating');
      return;
    }

    if (!ratingType) {
      setError('Please select a rating type');
      return;
    }

    setSubmitting(true);
    setError('');
    setSuccess('');

    try {
      // Prepare rating data according to backend requirements
      const ratingData = {
        rating: Number(rating),
        comment: comment.trim() || '',
        rating_type: ratingType,
        [selectedType === 'lecturer' ? 'lecturer_id' : 'course_id']: parseInt(selectedId)
      };

      console.log('Submitting rating:', ratingData);

      const response = await apiMethods.submitRating(ratingData);

      if (response.success) {
        setSuccess('Rating submitted successfully!');
        // Reset form
        setSelectedId('');
        setRating(5);
        setComment('');
        setRatingType('teaching');
        // Refresh ratings
        fetchData();
      } else {
        setError(response.message || 'Failed to submit rating');
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      setError(error.message || 'Failed to submit rating. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const resetForm = () => {
    setSelectedId('');
    setRating(5);
    setComment('');
    setRatingType('teaching');
    setError('');
    setSuccess('');
  };

  // Check if user has already rated this lecturer/course for the same rating type
  const hasRated = (type, id, currentRatingType) => {
    return myRatings.some(rating => 
      (type === 'lecturer' && rating.lecturer_id == id && rating.rating_type === currentRatingType) ||
      (type === 'course' && rating.course_id == id && rating.rating_type === currentRatingType)
    );
  };

  // Filter available options based on what hasn't been rated for current rating type
  const getAvailableOptions = () => {
    const items = selectedType === 'lecturer' ? lecturers : courses;
    return items.filter(item => !hasRated(selectedType, item.id, ratingType));
  };

  // Get display name for selected item
  const getSelectedItemName = () => {
    if (!selectedId) return '';
    
    const items = selectedType === 'lecturer' ? lecturers : courses;
    const item = items.find(i => i.id == selectedId);
    
    if (!item) return '';
    
    if (selectedType === 'lecturer') {
      return `${item.first_name} ${item.last_name}`;
    } else {
      return `${item.code} - ${item.course_name || item.name}`;
    }
  };

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <h2 className="text-primary mb-4">
            <i className="fas fa-star me-2"></i>
            {user?.role === 'Lecturer' ? 'My Ratings' : 'Rate & Review'}
          </h2>

          {/* Success Message */}
          {success && (
            <div className="alert alert-success alert-dismissible fade show" role="alert">
              <i className="fas fa-check-circle me-2"></i>
              {success}
              <button type="button" className="btn-close" onClick={() => setSuccess('')}></button>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="alert alert-danger alert-dismissible fade show" role="alert">
              <i className="fas fa-exclamation-triangle me-2"></i>
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          <div className="row">
            {/* Rating Form - Only for Students */}
            {user?.role === 'Student' && (
              <div className="col-lg-6 mb-4">
                <div className="card shadow">
                  <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
                    <h5 className="mb-0">
                      <i className="fas fa-plus-circle me-2"></i>
                      Submit Rating
                    </h5>
                    <button 
                      type="button" 
                      className="btn btn-sm btn-light"
                      onClick={resetForm}
                      disabled={submitting}
                    >
                      <i className="fas fa-redo me-1"></i>
                      Reset
                    </button>
                  </div>
                  <div className="card-body">
                    <form onSubmit={handleSubmit}>
                      {/* Rating Type Selection */}
                      <div className="mb-3">
                        <label className="form-label fw-bold">What are you rating? *</label>
                        <div className="btn-group w-100" role="group">
                          <button
                            type="button"
                            className={`btn ${selectedType === 'lecturer' ? 'btn-primary' : 'btn-outline-primary'}`}
                            onClick={() => {
                              setSelectedType('lecturer');
                              setSelectedId('');
                            }}
                          >
                            <i className="fas fa-user-tie me-2"></i>
                            Lecturer
                          </button>
                          <button
                            type="button"
                            className={`btn ${selectedType === 'course' ? 'btn-primary' : 'btn-outline-primary'}`}
                            onClick={() => {
                              setSelectedType('course');
                              setSelectedId('');
                            }}
                          >
                            <i className="fas fa-book me-2"></i>
                            Course
                          </button>
                        </div>
                      </div>

                      {/* Rating Category */}
                      <div className="mb-3">
                        <label className="form-label fw-bold">Rating Category *</label>
                        <select
                          className="form-select"
                          value={ratingType}
                          onChange={(e) => {
                            setRatingType(e.target.value);
                            setSelectedId(''); // Reset selection when category changes
                          }}
                          required
                          disabled={submitting}
                        >
                          <option value="">Select rating category</option>
                          {ratingTypes.map(type => (
                            <option key={type.value} value={type.value}>
                              {type.label}
                            </option>
                          ))}
                        </select>
                      </div>

                      {/* Lecturer/Course Selection */}
                      <div className="mb-3">
                        <label className="form-label fw-bold">
                          Select {selectedType === 'lecturer' ? 'Lecturer' : 'Course'} *
                        </label>
                        <select
                          className="form-select"
                          value={selectedId}
                          onChange={(e) => setSelectedId(e.target.value)}
                          required
                          disabled={submitting || !ratingType}
                        >
                          <option value="">Choose a {selectedType}</option>
                          {getAvailableOptions().map(item => (
                            <option key={item.id} value={item.id}>
                              {selectedType === 'lecturer' 
                                ? `${item.first_name} ${item.last_name}`
                                : `${item.code} - ${item.course_name || item.name}`
                              }
                            </option>
                          ))}
                        </select>
                        {getAvailableOptions().length === 0 && ratingType && (
                          <div className="text-info mt-2">
                            <i className="fas fa-info-circle me-1"></i>
                            <small>No {selectedType}s available to rate for this category</small>
                          </div>
                        )}
                      </div>

                      {/* Rating Stars */}
                      <div className="mb-3">
                        <label className="form-label fw-bold">Rating *</label>
                        <div className="d-flex align-items-center mb-2">
                          {[1, 2, 3, 4, 5].map(star => (
                            <button
                              key={star}
                              type="button"
                              className="btn btn-link p-1"
                              onClick={() => setRating(star)}
                              disabled={submitting}
                            >
                              <i
                                className={`fas fa-star fa-2x ${
                                  star <= rating ? 'text-warning' : 'text-muted'
                                }`}
                              ></i>
                            </button>
                          ))}
                          <span className="ms-3 fw-bold fs-5 text-primary">
                            {rating}/5
                          </span>
                        </div>
                        <div className="text-muted small">
                          {rating === 5 && 'Excellent - Outstanding performance'}
                          {rating === 4 && 'Very Good - Above expectations'}
                          {rating === 3 && 'Good - Meets expectations'}
                          {rating === 2 && 'Fair - Below expectations'}
                          {rating === 1 && 'Poor - Unsatisfactory'}
                        </div>
                      </div>

                      {/* Comment */}
                      <div className="mb-4">
                        <label className="form-label fw-bold">Comment (optional)</label>
                        <textarea
                          className="form-control"
                          rows="3"
                          value={comment}
                          onChange={(e) => setComment(e.target.value)}
                          placeholder={`Share your thoughts about this ${selectedType}...`}
                          disabled={submitting}
                          maxLength={500}
                        ></textarea>
                        <div className="form-text text-end">
                          {comment.length}/500 characters
                        </div>
                      </div>

                      {/* Submit Button */}
                      <button
                        type="submit"
                        className="btn btn-primary w-100 py-2"
                        disabled={
                          submitting || 
                          !selectedId || 
                          !ratingType ||
                          hasRated(selectedType, selectedId, ratingType)
                        }
                      >
                        {submitting ? (
                          <>
                            <div className="spinner-border spinner-border-sm me-2"></div>
                            Submitting...
                          </>
                        ) : (
                          <>
                            <i className="fas fa-star me-2"></i>
                            Submit Rating
                          </>
                        )}
                      </button>

                      {/* Already rated warning */}
                      {selectedId && hasRated(selectedType, selectedId, ratingType) && (
                        <div className="alert alert-warning mt-3 mb-0 py-2">
                          <i className="fas fa-exclamation-triangle me-2"></i>
                          You have already rated this {selectedType} for {ratingTypes.find(t => t.value === ratingType)?.label}
                        </div>
                      )}
                    </form>
                  </div>
                </div>
              </div>
            )}

            {/* Ratings History */}
            <div className={user?.role === 'Student' ? 'col-lg-6' : 'col-12'}>
              <div className="card shadow">
                <div className="card-header bg-success text-white d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">
                    <i className="fas fa-history me-2"></i>
                    {user?.role === 'Lecturer' ? 'Ratings Received' : 'My Ratings'} 
                    ({myRatings.length})
                  </h5>
                  <button 
                    className="btn btn-sm btn-light"
                    onClick={fetchData}
                    disabled={loading}
                  >
                    <i className="fas fa-sync-alt"></i>
                  </button>
                </div>
                <div className="card-body">
                  {loading ? (
                    <div className="text-center py-4">
                      <div className="spinner-border text-primary"></div>
                      <p className="mt-2">Loading ratings...</p>
                    </div>
                  ) : myRatings.length === 0 ? (
                    <div className="text-center py-5">
                      <i className="fas fa-star fa-3x text-muted mb-3"></i>
                      <h5 className="text-muted">
                        {user?.role === 'Lecturer' ? 'No ratings received yet' : 'No ratings submitted yet'}
                      </h5>
                      <p className="text-muted">
                        {user?.role === 'Lecturer' 
                          ? 'Ratings from students will appear here' 
                          : 'Start by rating a lecturer or course'
                        }
                      </p>
                    </div>
                  ) : (
                    <div className="list-group list-group-flush">
                      {myRatings.map((ratingItem) => (
                        <div key={ratingItem.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div className="flex-grow-1">
                              <h6 className="mb-1 text-primary">
                                {user?.role === 'Lecturer' 
                                  ? `${ratingItem.course_name} - ${ratingItem.student_name || 'Student'}`
                                  : `${ratingItem.lecturer_name || 'Lecturer'} - ${ratingItem.course_name}`
                                }
                              </h6>
                              <div className="small text-muted mb-1">
                                {ratingItem.class_name && `Class: ${ratingItem.class_name} | `}
                                <span className="badge bg-info text-capitalize">
                                  {ratingItem.rating_type?.replace('_', ' ') || 'Rating'}
                                </span>
                              </div>
                            </div>
                            <div className="text-end">
                              <div className="fw-bold text-warning fs-5">
                                {[1, 2, 3, 4, 5].map(star => (
                                  <i
                                    key={star}
                                    className={`fas fa-star ${
                                      star <= ratingItem.rating ? 'text-warning' : 'text-muted'
                                    }`}
                                  ></i>
                                ))}
                                <span className="ms-1">({ratingItem.rating})</span>
                              </div>
                              <small className="text-muted">
                                {ratingItem.created_at ? new Date(ratingItem.created_at).toLocaleDateString() : 'Recent'}
                              </small>
                            </div>
                          </div>
                          
                          {ratingItem.comment && (
                            <div className="bg-light p-3 rounded mt-2">
                              <p className="mb-0 small">
                                <i className="fas fa-comment text-muted me-1"></i>
                                "{ratingItem.comment}"
                              </p>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
