import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../utils/auth';
import { api, API_ENDPOINTS } from '../utils/api';

export default function Register() {
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'Lecturer'
  });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [touched, setTouched] = useState({});

  const navigate = useNavigate();
  const auth = useAuth();

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
    setTouched(prev => ({ ...prev, [name]: true }));

    // Clear error when user starts typing
    if (error) setError('');
  };

  const validateField = (name, value) => {
    switch (name) {
      case 'email':
        return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value) ? '' : 'Please enter a valid email address';
      case 'password':
        return value.length >= 6 ? '' : 'Password must be at least 6 characters';
      case 'confirmPassword':
        return value === form.password ? '' : 'Passwords do not match';
      case 'first_name':
      case 'last_name':
        return value.trim() ? '' : 'This field is required';
      default:
        return '';
    }
  };

  const getFieldError = (name) => {
    return touched[name] ? validateField(name, form[name]) : '';
  };

  const isFormValid = () => {
    return Object.keys(form).every(key => 
      key === 'confirmPassword' || !validateField(key, form[key])
    ) && form.password === form.confirmPassword;
  };

  async function submit(e) {
    e.preventDefault();
    setLoading(true);
    setError('');

    // Mark all fields as touched
    setTouched(Object.keys(form).reduce((acc, key) => ({ ...acc, [key]: true }), {}));

    if (!isFormValid()) {
      setError('Please fix the form errors before submitting.');
      setLoading(false);
      return;
    }

    try {
      const { confirmPassword, ...submitData } = form;
      const res = await api.post(API_ENDPOINTS.REGISTER, submitData);

      if (res.success) {
        // Auto-login after successful registration
        auth.saveLogin(res.data.token, res.data.user);
        navigate('/', { replace: true });
      } else {
        setError(res.error || 'Registration failed. Please try again.');
      }
    } catch (err) {
      setError(err.message || 'Registration failed. Please try again.');
    } finally {
      setLoading(false);
    }
  }

  const FormField = ({ label, name, type = 'text', placeholder, children, ...props }) => {
    const error = getFieldError(name);
    return (
      <div className="mb-3">
        <label className="form-label fw-semibold text-dark">{label}</label>
        {children || (
          <input 
            type={type}
            name={name}
            className={`form-control ${error ? 'is-invalid' : touched[name] ? 'is-valid' : ''}`}
            value={form[name]}
            onChange={handleChange}
            placeholder={placeholder}
            disabled={loading}
            {...props}
          />
        )}
        {error && <div className="invalid-feedback d-block">{error}</div>}
      </div>
    );
  };

  return (
    <div className="container-fluid py-5 bg-light min-vh-100">
      <div className="row justify-content-center">
        <div className="col-md-8 col-lg-6 col-xl-5">
          <div className="card shadow border-0 rounded-4 overflow-hidden">
            <div className="card-header bg-gradient-info text-white text-center py-4 border-0">
              <div className="mb-3">
                <i className="fas fa-user-plus fa-3x mb-3"></i>
                <h4 className="mb-2 fw-bold">Join LUCT Reporting</h4>
                <small className="opacity-75">Create your account to start reporting</small>
              </div>
            </div>

            <div className="card-body p-4 p-md-5">
              {error && (
                <div className="alert alert-danger d-flex align-items-center rounded-3" role="alert">
                  <i className="fas fa-exclamation-triangle me-2"></i>
                  <div>{error}</div>
                </div>
              )}

              <form onSubmit={submit} className="needs-validation" noValidate>
                <div className="row">
                  <div className="col-md-6">
                    <FormField
                      label="First Name"
                      name="first_name"
                      placeholder="Enter your first name"
                      required
                    />
                  </div>

                  <div className="col-md-6">
                    <FormField
                      label="Last Name"
                      name="last_name"
                      placeholder="Enter your last name"
                      required
                    />
                  </div>
                </div>

                <FormField
                  label="Email Address"
                  name="email"
                  type="email"
                  placeholder="Enter your email address"
                  required
                />

                <div className="row">
                  <div className="col-md-6">
                    <FormField
                      label="Password"
                      name="password"
                      type="password"
                      placeholder="Create a password"
                      minLength="6"
                      required
                    />
                  </div>

                  <div className="col-md-6">
                    <FormField
                      label="Confirm Password"
                      name="confirmPassword"
                      type="password"
                      placeholder="Confirm your password"
                      required
                    />
                  </div>
                </div>

                <FormField
                  label="Role"
                  name="role"
                >
                  <select 
                    className={`form-select ${touched.role ? 'is-valid' : ''}`}
                    name="role"
                    value={form.role} 
                    onChange={handleChange}
                    disabled={loading}
                  >
                    <option value="Lecturer">Lecturer</option>
                    <option value="Student">Student</option>
                    <option value="PRL">PRL</option>
                    <option value="PL">PL</option>
                  </select>
                  <div className="form-text">
                    Select your role in the institution. This determines your access level.
                  </div>
                </FormField>

                <button 
                  className="btn btn-info text-white btn-lg w-100 py-3 fw-semibold rounded-pill shadow-sm mt-3" 
                  type="submit"
                  disabled={loading || !isFormValid()}
                >
                  {loading ? (
                    <>
                      <span className="spinner-border spinner-border-sm me-2" role="status"></span>
                      Creating Account...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-user-plus me-2"></i>
                      Create Account
                    </>
                  )}
                </button>
              </form>

              <div className="text-center mt-4 pt-3 border-top">
                <p className="text-muted mb-0">
                  Already have an account?{' '}
                  <Link to="/login" className="text-info text-decoration-none fw-semibold">
                    Sign in here
                  </Link>
                </p>
              </div>
            </div>
          </div>

          {/* Role Information Card */}
          <div className="card mt-4 border-0 rounded-4 bg-light">
            <div className="card-body p-3">
              <small className="text-muted">
                <i className="fas fa-info-circle me-1"></i>
                <strong>Role Information:</strong> Lecturers can submit reports, Students can view reports, 
                PRL/PL have administrative access.
              </small>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
