import React, { useState, useEffect } from 'react';
import { useAuth } from '../utils/auth';
import { apiMethods } from '../utils/api';

export default function Rating() {
  const [lecturers, setLecturers] = useState([]);
  const [courses, setCourses] = useState([]);
  const [classes, setClasses] = useState([]);
  const [myRatings, setMyRatings] = useState([]);
  const [selectedType, setSelectedType] = useState('lecturer');
  const [selectedId, setSelectedId] = useState('');
  const [selectedClassName, setSelectedClassName] = useState('');
  const [rating, setRating] = useState(5);
  const [comment, setComment] = useState('');
  const [loading, setLoading] = useState(true);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const { user } = useAuth();

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [lecturersRes, coursesRes, classesRes] = await Promise.all([
        apiMethods.getLecturers(),
        apiMethods.getCourses(),
        apiMethods.getClasses()
      ]);

      if (lecturersRes.success) setLecturers(lecturersRes.data || []);
      if (coursesRes.success) setCourses(coursesRes.data || []);
      if (classesRes.success) setClasses(classesRes.data || []);

      // Try to get ratings, but don't fail if it errors
      try {
        const ratingsRes = user?.role === 'Lecturer' 
          ? await apiMethods.getLecturerRatings()
          : await apiMethods.getMyRatings();
        
        if (ratingsRes.success) setMyRatings(ratingsRes.data || []);
      } catch (ratingsError) {
        console.warn('Could not load ratings:', ratingsError);
        setMyRatings([]);
      }
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to load some data');
    } finally {
      setLoading(false);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    // Validation
    if (!selectedId) {
      setError(`Please select a ${selectedType}`);
      return;
    }
    
    if (!selectedClassName) {
      setError('Please select a class');
      return;
    }

    if (rating < 1 || rating > 5) {
      setError('Please select a valid rating between 1 and 5');
      return;
    }

    setSubmitting(true);
    setError('');
    setSuccess('');

    try {
      // Create proper rating data structure
      const ratingData = {
        rating: Number(rating),
        comment: comment.trim(),
        class_name: selectedClassName
      };

      // Add either lecturer_id or course_id based on selection
      if (selectedType === 'lecturer') {
        ratingData.lecturer_id = selectedId;
      } else {
        ratingData.course_id = selectedId;
      }

      console.log('Submitting rating data:', ratingData);

      const response = await apiMethods.submitRating(ratingData);
      
      if (response.success) {
        setSuccess('Rating submitted successfully!');
        setRating(5);
        setComment('');
        setSelectedId('');
        setSelectedClassName('');
        fetchData(); // Refresh ratings
      } else {
        setError(response.message || 'Failed to submit rating');
      }
    } catch (error) {
      console.error('Error submitting rating:', error);
      setError(error.message || 'Failed to submit rating. Please try again.');
    } finally {
      setSubmitting(false);
    }
  };

  const hasRated = (type, id, className) => {
    return myRatings.some(rating => 
      (type === 'lecturer' && rating.lecturer_id === id && rating.class_name === className) ||
      (type === 'course' && rating.course_id === id && rating.class_name === className)
    );
  };

  const resetForm = () => {
    setSelectedId('');
    setSelectedClassName('');
    setRating(5);
    setComment('');
    setError('');
    setSuccess('');
  };

  return (
    <div className="container-fluid py-4">
      <div className="row">
        <div className="col-12">
          <h2 className="text-primary mb-4">
            <i className="fas fa-star me-2"></i>
            Ratings & Reviews
          </h2>

          {/* Success Message */}
          {success && (
            <div className="alert alert-success alert-dismissible fade show" role="alert">
              <i className="fas fa-check-circle me-2"></i>
              {success}
              <button type="button" className="btn-close" onClick={() => setSuccess('')}></button>
            </div>
          )}

          {/* Error Message */}
          {error && (
            <div className="alert alert-danger alert-dismissible fade show" role="alert">
              <i className="fas fa-exclamation-triangle me-2"></i>
              {error}
              <button type="button" className="btn-close" onClick={() => setError('')}></button>
            </div>
          )}

          <div className="row">
            <div className="col-lg-6 mb-4">
              <div className="card shadow">
                <div className="card-header bg-primary text-white d-flex justify-content-between align-items-center">
                  <h5 className="mb-0">
                    <i className="fas fa-plus-circle me-2"></i>
                    Submit Rating
                  </h5>
                  <button 
                    type="button" 
                    className="btn btn-sm btn-light"
                    onClick={resetForm}
                    disabled={submitting}
                  >
                    <i className="fas fa-redo me-1"></i>
                    Reset
                  </button>
                </div>
                <div className="card-body">
                  <form onSubmit={handleSubmit}>
                    <div className="mb-3">
                      <label className="form-label fw-bold">Rate a:</label>
                      <div className="btn-group w-100" role="group">
                        <button
                          type="button"
                          className={`btn ${selectedType === 'lecturer' ? 'btn-primary' : 'btn-outline-primary'}`}
                          onClick={() => {
                            setSelectedType('lecturer');
                            setSelectedId('');
                            setSelectedClassName('');
                          }}
                        >
                          <i className="fas fa-user me-1"></i>
                          Lecturer
                        </button>
                        <button
                          type="button"
                          className={`btn ${selectedType === 'course' ? 'btn-primary' : 'btn-outline-primary'}`}
                          onClick={() => {
                            setSelectedType('course');
                            setSelectedId('');
                            setSelectedClassName('');
                          }}
                        >
                          <i className="fas fa-book me-1"></i>
                          Course
                        </button>
                      </div>
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">
                        Select {selectedType === 'lecturer' ? 'Lecturer' : 'Course'}:
                      </label>
                      <select
                        className="form-select"
                        value={selectedId}
                        onChange={(e) => {
                          setSelectedId(e.target.value);
                          setSelectedClassName(''); // Reset class when changing lecturer/course
                        }}
                        required
                        disabled={submitting}
                      >
                        <option value="">Choose a {selectedType}</option>
                        {(selectedType === 'lecturer' ? lecturers : courses).map(item => (
                          <option key={item.id} value={item.id}>
                            {selectedType === 'lecturer' 
                              ? `${item.first_name} ${item.last_name}`
                              : `${item.course_code} - ${item.course_name}`
                            }
                          </option>
                        ))}
                      </select>
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Select Class:</label>
                      <select
                        className="form-select"
                        value={selectedClassName}
                        onChange={(e) => setSelectedClassName(e.target.value)}
                        required
                        disabled={submitting || !selectedId}
                      >
                        <option value="">Choose a class</option>
                        {classes.map(classItem => (
                          <option key={classItem.id} value={classItem.class_name}>
                            {classItem.class_name}
                          </option>
                        ))}
                      </select>
                      {selectedId && selectedClassName && hasRated(selectedType, selectedId, selectedClassName) && (
                        <div className="text-warning mt-2">
                          <i className="fas fa-exclamation-triangle me-1"></i>
                          <small>You have already rated this {selectedType} for this class</small>
                        </div>
                      )}
                    </div>

                    <div className="mb-3">
                      <label className="form-label fw-bold">Rating:</label>
                      <div className="d-flex align-items-center mb-2">
                        {[1, 2, 3, 4, 5].map(star => (
                          <button
                            key={star}
                            type="button"
                            className="btn btn-link p-1"
                            onClick={() => setRating(star)}
                            disabled={submitting}
                          >
                            <i
                              className={`fas fa-star fa-2x ${
                                star <= rating ? 'text-warning' : 'text-muted'
                              }`}
                            ></i>
                          </button>
                        ))}
                        <span className="ms-3 fw-bold fs-5">{rating}/5</span>
                      </div>
                      <div className="text-muted small">
                        {rating === 1 && "Poor"}
                        {rating === 2 && "Fair"}
                        {rating === 3 && "Good"}
                        {rating === 4 && "Very Good"}
                        {rating === 5 && "Excellent"}
                      </div>
                    </div>

                    <div className="mb-4">
                      <label className="form-label fw-bold">Comment (optional):</label>
                      <textarea
                        className="form-control"
                        rows="3"
                        value={comment}
                        onChange={(e) => setComment(e.target.value)}
                        placeholder="Share your experience, feedback, or suggestions..."
                        disabled={submitting}
                        maxLength={500}
                      ></textarea>
                      <div className="form-text text-end">
                        {comment.length}/500 characters
                      </div>
                    </div>

                    <button
                      type="submit"
                      className="btn btn-primary w-100 py-2"
                      disabled={
                        submitting || 
                        !selectedId || 
                        !selectedClassName || 
                        hasRated(selectedType, selectedId, selectedClassName)
                      }
                    >
                      {submitting ? (
                        <>
                          <div className="spinner-border spinner-border-sm me-2"></div>
                          Submitting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-star me-2"></i>
                          Submit Rating
                        </>
                      )}
                    </button>

                    {hasRated(selectedType, selectedId, selectedClassName) && (
                      <div className="alert alert-warning mt-3 mb-0 py-2">
                        <i className="fas fa-info-circle me-2"></i>
                        You can only rate each {selectedType} once per class
                      </div>
                    )}
                  </form>
                </div>
              </div>
            </div>

            <div className="col-lg-6">
              <div className="card shadow">
                <div className="card-header bg-success text-white">
                  <h5 className="mb-0">
                    <i className="fas fa-history me-2"></i>
                    My Ratings ({myRatings.length})
                  </h5>
                </div>
                <div className="card-body">
                  {loading ? (
                    <div className="text-center py-4">
                      <div className="spinner-border text-primary"></div>
                      <p className="mt-2">Loading ratings...</p>
                    </div>
                  ) : myRatings.length === 0 ? (
                    <div className="text-center py-5">
                      <i className="fas fa-star fa-3x text-muted mb-3"></i>
                      <h5 className="text-muted">No ratings submitted yet</h5>
                      <p className="text-muted">Start by rating a lecturer or course</p>
                    </div>
                  ) : (
                    <div className="list-group list-group-flush">
                      {myRatings.map((ratingItem) => (
                        <div key={ratingItem.id} className="list-group-item">
                          <div className="d-flex justify-content-between align-items-start mb-2">
                            <div>
                              <h6 className="mb-1 text-primary">
                                {ratingItem.lecturer_name || ratingItem.course_name}
                              </h6>
                              <span className="badge bg-info me-2">
                                {ratingItem.lecturer_name ? 'Lecturer' : 'Course'}
                              </span>
                              <span className="badge bg-secondary">
                                Class: {ratingItem.class_name}
                              </span>
                            </div>
                            <small className="text-muted">
                              {ratingItem.created_at ? new Date(ratingItem.created_at).toLocaleDateString() : 'Recent'}
                            </small>
                          </div>
                          
                          <div className="mb-2">
                            {[1, 2, 3, 4, 5].map(star => (
                              <i
                                key={star}
                                className={`fas fa-star ${
                                  star <= ratingItem.rating ? 'text-warning' : 'text-muted'
                                } me-1`}
                              ></i>
                            ))}
                            <span className="fw-bold ms-1">{ratingItem.rating}/5</span>
                          </div>
                          
                          {ratingItem.comment && (
                            <div className="bg-light p-2 rounded">
                              <p className="mb-0 small">
                                <i className="fas fa-comment text-muted me-1"></i>
                                "{ratingItem.comment}"
                              </p>
                            </div>
                          )}
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
