import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../utils/auth';
import { api, API_ENDPOINTS } from '../utils/api';

export default function Register() {
  const [form, setForm] = useState({
    first_name: '',
    last_name: '',
    email: '',
    password: '',
    confirmPassword: '',
    role: 'Lecturer'
  });
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState({});
  const [touched, setTouched] = useState({});

  const navigate = useNavigate();
  const auth = useAuth();

  const validateField = (name, value) => {
    const newErrors = { ...errors };

    switch (name) {
      case 'first_name':
        if (!value.trim()) {
          newErrors.first_name = 'First name is required';
        } else if (value.trim().length < 2) {
          // Only show error after user has finished typing (on blur)
          if (touched[name]) {
            newErrors.first_name = 'First name must be at least 2 characters';
          }
        } else {
          delete newErrors.first_name;
        }
        break;

      case 'last_name':
        if (!value.trim()) {
          newErrors.last_name = 'Last name is required';
        } else if (value.trim().length < 2) {
          if (touched[name]) {
            newErrors.last_name = 'Last name must be at least 2 characters';
          }
        } else {
          delete newErrors.last_name;
        }
        break;

      case 'email':
        if (!value.trim()) {
          newErrors.email = 'Email is required';
        } else if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(value)) {
          if (touched[name]) {
            newErrors.email = 'Please enter a valid email address';
          }
        } else {
          delete newErrors.email;
        }
        break;

      case 'password':
        if (!value) {
          newErrors.password = 'Password is required';
        } else if (value.length < 6) {
          if (touched[name]) {
            newErrors.password = 'Password must be at least 6 characters';
          }
        } else {
          delete newErrors.password;
        }
        break;

      case 'confirmPassword':
        if (!value) {
          newErrors.confirmPassword = 'Please confirm your password';
        } else if (value !== form.password) {
          if (touched[name]) {
            newErrors.confirmPassword = 'Passwords do not match';
          }
        } else {
          delete newErrors.confirmPassword;
        }
        break;

      default:
        break;
    }

    return newErrors;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));

    // Only validate for immediate feedback on password match and email format
    if (name === 'confirmPassword' || name === 'email') {
      const newErrors = validateField(name, value);
      setErrors(newErrors);
    }
  };

  const handleBlur = (e) => {
    const { name, value } = e.target;
    setTouched(prev => ({ ...prev, [name]: true }));

    const newErrors = validateField(name, value);
    setErrors(newErrors);
  };

  const validateForm = () => {
    const newErrors = {};

    // Validate all fields on submit
    Object.keys(form).forEach(key => {
      if (key !== 'role') {
        const fieldErrors = validateField(key, form[key]);
        Object.assign(newErrors, fieldErrors);
      }
    });

    setErrors(newErrors);

    // Mark all fields as touched to show errors
    const allTouched = {
      first_name: true,
      last_name: true,
      email: true,
      password: true,
      confirmPassword: true,
      role: true
    };
    setTouched(allTouched);

    return Object.keys(newErrors).length === 0;
  };

  const getFieldClass = (fieldName) => {
    if (!touched[fieldName]) return 'form-control';
    return errors[fieldName] ? 'form-control is-invalid' : 'form-control is-valid';
  };

  const getSelectClass = (fieldName) => {
    return 'form-select'; // Always normal for select
  };

  async function submit(e) {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setLoading(true);
    setErrors({});

    try {
      const { confirmPassword, ...submitData } = form;
      const response = await api.post(API_ENDPOINTS.REGISTER, submitData);

      if (response.success) {
        // Auto-login after successful registration
        auth.saveLogin(response.data.token, response.data.user);
        navigate('/', { replace: true });
      } else {
        setErrors({ submit: response.message || 'Registration failed. Please try again.' });
      }
    } catch (err) {
      console.error('Registration error:', err);
      let errorMessage = 'Registration failed. Please try again.';

      if (err.message && err.message.includes('Email already')) {
        errorMessage = 'This email is already registered. Please use a different email.';
      } else if (err.message) {
        errorMessage = err.message;
      }

      setErrors({ submit: errorMessage });
    } finally {
      setLoading(false);
    }
  }

  const FormField = ({ label, name, type = 'text', placeholder, children, ...props }) => {
    const error = errors[name];
    const isTouched = touched[name];

    return (
      <div className="mb-3">
        <label className="form-label fw-semibold text-dark">
          {label} {props.required && <span className="text-danger">*</span>}
        </label>
        {children || (
          <input 
            type={type}
            name={name}
            className={getFieldClass(name)}
            value={form[name]}
            onChange={handleChange}
            onBlur={handleBlur}
            placeholder={placeholder}
            disabled={loading}
            {...props}
          />
        )}
        {isTouched && error && <div className="invalid-feedback d-block">{error}</div>}
      </div>
    );
  };

  return (
    <div className="container-fluid py-5 bg-light min-vh-100">
